        diag_log text format ["----- Starting Testing %1 [%2] -----", _testName, _testFile];
        private _return = ([nil] apply (compile preProcessFileLineNumbers _testFile)) select 0;
        if ((isNil "_return") || {!(_return isEqualTo true)}) then {
            systemChat format ["Test [%1] Failed", _testName];
            diag_log text format ["----- Finished Testing %1 [Failed] -----", _testName];
            _fails pushBack _testName;
        } else {
            diag_log text format ["----- Finished Testing %1 [Passed] -----", _testName];
        };
    };
} forEach (configProperties [configFile >> "ACE_Tests"]);

INFO_1("ace_common_fnc_runTests finished in %1 ms", (1000 * (diag_tickTime - _startTime)) toFixed 1);
INFO_2("[%1 / %2] Tests Passed", (_total - (count _fails)), _total);
if (!(_fails isEqualTo [])) then {
    INFO_1("Failed: %1", _fails);
};

/*
 * Author: esteldunedain, based on Killzone-Kid code
 * Removes quotation marks to avoid exploits and optionally html tags from text to avoid conflicts with structured text.
 *
 * Arguments:
 * 0: Source string <STRING>
 * 1: Remove html tags (default: false) <BOOL>
 *
 * Return Value:
 * Sanitized string <STRING>
 *
 * Example:
 * ["<CoolGroup>CoolGuy", true] call ace_common_fnc_sanitizeString;
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_string", ["_removeTags", false]];

private _array = [];

{
    switch _x do {
        case 60 : { // less than symbol: `<`
            if (_removeTags) then {
                _array append toArray "&lt;";
            } else {
                _array pushBack _x;
            };
        };
        case 62 : { // greater than symbol: `>`
            if (_removeTags) then {
                _array append toArray "&gt;";
            } else {
                _array pushBack _x;
            };
        };

        case 34 : {
        };

        case 39 : {
        };

        default {
            _array pushBack _x;
        };
    };
    false
} count toArray _string;

toString _array // return

