TRACE_2("bitArray",_statusIndex,_effectBoolArray);

private _activeEffects = [];
{
    if (_x) then {
        _activeEffects pushBack (_statusReasons select _forEachIndex);
    };
} forEach _effectBoolArray;

//non-empty array - true effect
[true, _activeEffects]

