    GVAR(publishVarNames) = [];
    GVAR(publishNextTime) = 1e7;

    GVAR(publishSchedId) = [{
        if (diag_tickTime > GVAR(publishNextTime)) then {
            {
                _x params ["_unit", "_varName"];
                _unit setVariable [_varName, _unit getVariable _varName, true];
                false
            } count GVAR(publishVarNames);

            GVAR(publishVarNames) = [];
            GVAR(publishNextTime) = 1e7;
        };
    }, 0, []] call CBA_fnc_addPerFrameHandler;
};

// If the variable is not on the list
if (GVAR(publishVarNames) find [_unit, _varName] == -1) exitWith {
    GVAR(publishVarNames) pushBack [_unit, _varName];
    GVAR(publishNextTime) = GVAR(publishNextTime) min (diag_tickTime + _maxDelay);
};

// If the variable is on the list
GVAR(publishNextTime) = GVAR(publishNextTime) min (diag_tickTime + _maxDelay);

/*
 * Author: commy2
 * Converts number to binary number
 *
 * Arguments:
 * A number <NUMBER>
 *
 * Return Value:
 * A binary number as string <STRING>
 *
 * Example:
 * [5] call ace_common_fnc_toBin
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_number", ["_minLength", 1]];

private _sign = ["", "-"] select (_number < 0);

_number = round abs _number;
private _bin = ["", "0"] select (_number == 0);

while {_number > 0} do {
    private _rest = str (_number mod 2);
    _number = floor (_number / 2);
    _bin = _rest + _bin;
};

while {count toArray _bin < _minLength} do {
    _bin = "0" + _bin;
};

_sign + _bin // return

