/*
 * Author: Rocko, Ruthberg
 *
 * Deploys the concertina wire
 *
 * Arguments:
 * 0: wire coil <OBJECT>
 * 1: unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [coil, bob] call ace_concertina_wire_fnc_deploy
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_wirecoil", "_unit"];

private _wireNoGeo = "ACE_ConcertinaWireNoGeo" createVehicle [0,0,0];
{
    _wireNoGeo animate [_x, 1];
} count WIRE_FAST;

GVAR(placer) = _unit;
private _dir = getDir _unit;
private _pos = getPosASL _unit;
private _wireNoGeoPos = _pos vectorAdd [1.1 * sin(_dir), 1.1 * cos(_dir), 0];

_wireNoGeo setDir _dir;
_wireNoGeo setPosASL _wireNoGeoPos;

deleteVehicle _wirecoil;

_unit setVariable [QGVAR(wireDeployed), false];

GVAR(deployPFH) = [{
    params ["_args", "_idPFH"];
    _args params ["_wireNoGeo", "_wireNoGeoPos", "_unit"];

    private _posStart = (_wireNoGeo modelToWorldVisual (_wireNoGeo selectionPosition "start")) call EFUNC(common,positionToASL);
    private _posEnd = (getPosASL _unit) vectorAdd (vectorDir _unit);
    private _dirVect = _posStart vectorDiff _posEnd;
    private _dir = _dirVect call CBA_fnc_vectDir;
    private _range = vectorMagnitude _dirVect;
    private _anim = 0 max (1 - (_range / 12));

    if (!(alive _unit) || _range >= 12 || (_unit getVariable [QGVAR(wireDeployed), false])) exitWith {
        private _wire = "ACE_ConcertinaWire" createvehicle [0, 0, 0];
        {
            _wire animate [_x, _anim];
        } count WIRE_FAST;

        [{
            params ["_args", "_idPFH"];
            _args params ["_wireNoGeo", "_wire", "_anim", "_dir", "_wireNoGeoPos"];
            if (_wire animationPhase "wire_2" == _anim) then {
                deleteVehicle _wireNoGeo;
                _wire setDir _dir;
                _wire setPosASL _wireNoGeoPos;
                [_idPFH] call CBA_fnc_removePerFrameHandler;
            };
        }, 0, [_wireNoGeo, _wire, _anim, _dir, _wireNoGeoPos]] call CBA_fnc_addPerFrameHandler;

        [_unit, "DefaultAction", _unit getVariable [QGVAR(Deploy), -1]] call EFUNC(Common,removeActionEventHandler);
        call EFUNC(interaction,hideMouseHint);

        [_idPFH] call CBA_fnc_removePerFrameHandler;
    };

    _wireNoGeo setDir _dir;
    {
        _wireNoGeo animate [_x, _anim];
    } count WIRE_FAST;
}, 0, [_wireNoGeo, _wireNoGeoPos, _unit]] call CBA_fnc_addPerFrameHandler;

[localize "STR_ACE_ROLLWIRE", "", ""] call EFUNC(interaction,showMouseHint);

GVAR(placer) setVariable [QGVAR(Deploy),
    [GVAR(placer), "DefaultAction",
    {GVAR(deployPFH) != -1},
    {GVAR(placer) setVariable [QGVAR(wireDeployed), true]}
] call EFUNC(common,AddActionEventHandler)];

/*
 * Author: Ruthberg
 *
 * Start dismounting the concertina wire
 *
 * Arguments:
 * 0: wire <OBJECT>
 * 1: unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [wire, bob] call ace_concertina_wire_fnc_dismount
 *
 * Public: No
 */
#include "script_component.hpp"

// If the cursorMenu is open, the loading bar will fail. If we execute the function one frame later, it will work fine
if (uiNamespace getVariable [QEGVAR(interact_menu,cursorMenuOpened),false]) exitWith {
    [{
        _this call FUNC(dismount);
    }, _this] call CBA_fnc_execNextFrame;
};
params ["_wire", "_unit"];

private _config = (configFile >> "CfgVehicles" >> typeOf _unit);
private _delay = [45, 30] select ([_unit] call EFUNC(common,isEngineer) || {[_unit] call EFUNC(common,isEOD)});

// TODO: Animation?
[
    _delay,
    [_wire],
    {(_this select 0) call FUNC(dismountSuccess)},
    {},
    localize "STR_ACE_UNROLLWIRE",
    {true},
    ["isnotinside"]
] call EFUNC(common,progressBar);

/*
 * Author: Rocko, Ruthberg
 *
 * Dismounts the concertina wire
 *
 * Arguments:
 * 0: wire <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [wire] call ace_concertina_wire_fnc_dismountSuccess
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_wire"];

{
    _wire animate [_x, 1];
} count WIRE_FAST;

[{
    params ["_args", "_idPFH"];
    _args params ["_wire"];

    if (_wire animationPhase "wire_2" == 1) then {

        private _dir = getDir _wire;
        private _pos = getPosASL _wire;

        private _wirecoil = "ACE_ConcertinaWireCoil" createvehicle [0, 0, 0];

        deleteVehicle _wire;

        _wirecoil setDir _dir;
        _wirecoil setPosASL _pos;
        _wirecoil setVelocity [0, 0, 0];

        [_idPFH] call CBA_fnc_removePerFrameHandler;
    };
}, 0, [_wire]] call CBA_fnc_addPerFrameHandler;

/*
 * Author: Rocko
 *
 * Handles wire and vehicle damage
 *
 * Arguments:
 * 0: wire <OBJECT>
 * 1: killer (vehicle) <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [wire, car] call ace_concertina_wire_fnc_handleKilled
 *
 * Public: No
 */
#include "script_component.hpp"
params ["_wire", "_killer"];
TRACE_2("params",_wire,_killer);

if (isNull _killer) then {
    _killer = _wire getVariable [QGVAR(lastDamager), objNull];
    if (isNull _killer) then {
        private _midPoint = ((_wire selectionPosition "start") vectorAdd (_wire selectionPosition "deploy")) vectorMultiply 0.5;
        {
            if ((vectorMagnitude velocity _x) > 0) exitWith {_killer = _x};
        } forEach (nearestObjects [(_wire modelToWorld _midPoint), ["Car"], 8]);
    };
};
if (isNull _killer || {_killer == _wire} || {_killer == gunner (vehicle _killer)}) exitWith {};

private _distance = _wire distance _killer;
if (_distance > 14 || {_distance < 2}) exitWith {}; // Fix if shooting wire

private _vehicle = vehicle _killer;

[QGVAR(vehicleDamage), [_wire, _vehicle], [_vehicle]] call CBA_fnc_targetEvent;

/*
 * Author: Rocko
 * Handles vehicle damage from hitting wire
 *
 * Arguments:
 * 0: wire <OBJECT>
 * 1: vehicle <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [wire, car] call ace_concertina_wire_fnc_vehicleDamage
 *
 * Public: No
 */
#include "script_component.hpp"
params ["_wire", "_vehicle"];

private _type = typeOf _wire;
private _mode = switch (_type) do {
case "ACE_ConcertinaWire": { 0 };
case "Land_Razorwire_F": { 1 };
    default { -1 };
};

if (_mode == -1) exitWith {};

// _mode = 0 = Single Coil
// _mode = 1 = Triple Coil

// --------------------------------
// L              M               R
//                                4.54929 (4)
//              6.13564 (6)
//9.78744 (10)

_type = typeOf _wire;
private _anim = _wire animationPhase "wire_2";
private _pos_w = getPos _wire;
private _dir_w = getDir _wire;

if (_mode == 0) then {
    private _found = false;
    _pos_w params ["_x","_y"];

    // Check if two Single coils are placed next to each other (i.e playes have built a big wire obstacle)
    private _wireCheckPosAr = [
    [_x + (sin (_dir_w + 90) * 1.5),_y + (cos (_dir_w + 90) * 1.5)],
    [(_x-(sin _dir_w)) + (sin (_dir_w + 90) * 1.5),(_y-(cos _dir_w)) + (cos (_dir_w + 90) * 1.5)],
    [_x + (sin (_dir_w - 90) * 1.5),_y + (cos (_dir_w - 90) * 1.5)],
    [(_x-(sin _dir_w)) + (sin (_dir_w - 90) * 1.5),(_y-(cos _dir_w)) + (cos (_dir_w - 90) * 1.5)]
    ];
    {
        _found = false;
        private _no = nearestObjects [_x, [typeOf _wire], 3];     //diag_log _no; diag_log ".....";
        _no = _no - [_wire];                            //diag_log _no;
        if (count _no > 0) exitWith {
            _found = true;                                //diag_log "found";
        };
    } forEach _wireCheckPosAr;
    // Double coil found!
    if (_found) then {
        _mode = 1;
    } else {
        // Randomly make a single coil also catch tanks, if speed is high
        if (_vehicle isKindOf "Tank" && {20 > random 100} && {speed _vehicle > 30}) then {
            _mode = 1;
        } else {
            if !(_vehicle isKindOf "Tank") then {
                _mode = 1;
            };
        };
    };
};

private _parts = [];

if (_mode == 1) then {
    switch (true) do {
    case (_vehicle isKindOf "Tank"): {
            _parts = ["ltrack","rtrack"];
        };
    case (_vehicle isKindOf "Wheeled_APC" || {_vehicle isKindOf "Car"}): {
            _parts = ["lfwheel","lf2wheel","lmwheel","lbwheel","rfwheel","rf2wheel","rmwheel","rbwheel"];
        };
    };
} else {
    switch (true) do {
    case (_vehicle isKindOf "Wheeled_APC" || {_vehicle isKindOf "Car"}): {
            _parts = ["lfwheel","lf2wheel","lmwheel","lbwheel","rfwheel","rf2wheel","rmwheel","rbwheel"];
        };
    };
};

if (canMove _vehicle) then {
    {
        private _selectionPart = "hit" + _x;
        if (isText(configFile >> "CfgVehicles" >> typeOf _vehicle >> "hitpoints" >> _selectionPart >> "name")) then {
            private _selection = getText(configFile >> "CfgVehicles" >> typeOf _vehicle  >> "hitpoints" >> _selectionPart >> "name");
            // TODO: Only the tires that have touched the wire should burst.
            _vehicle setHit [_selection, 1];
        };
    } forEach _parts;
};

if (_mode == 1) then {
    if (_vehicle isKindOf "StaticWeapon") exitWith {};
    [{
        params ["_vehicle", "_wire"];

        _vehicle setVelocity ((velocity _vehicle) vectorMultiply 0.75);

        // Set vehicle back in front of wire, since the wire will make the vehicle jump, and a wire with no geometry lod is undestructible and not recognizeable
        private _vPos = getPosASL _vehicle;
        private _vDir = getDir _vehicle;
        _vehicle setPosASL (_vPos vectorAdd [-0.35 * sin(_vDir), -0.35 * cos(_vDir), 0]);
        // TODO: Needs to be placed in safe distance to wire, so we do not constantly re - spawn new wires
    }, [_vehicle, _wire], 0.1] call CBA_fnc_waitAndExecute;
};

//TODO: Create broken geoless wire (two version)
//TODO: Make wire remains stop vehicles

