/*
 * Author: Garth 'L-H' de Wet
 * Adds an IED to the cellphone list
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Explosive <OBJECT>
 * 2: Magazine classname <STRING>
 * 3: Extra variables <ARRAY>
 *
 * Return Value:
 * None
 *
 * Example:
 * Handled by PlaceExplosive.
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_explosive", "_magazineClass", "_extra"];
TRACE_4("params",_unit,_explosive,_magazineClass,_extra);

// Config is the last item in the list of passed in items.
private _config = (_this select 3) select (count (_this select 3) - 1);

private _requiredItems = getArray(_config >> "requires");
private _hasRequired = true;
private _detonators = [_unit] call FUNC(getDetonators);
{
    if !(_x in _detonators) exitWith{
        _hasRequired = false;
    };
} count _requiredItems;

private _codeSet = false;
while {!_codeSet} do {
    private _code = str(round (random 9999));
    _count = 4 - count (toArray _code);
    while {_count > 0} do {
        _code = "0" + _code;
        _count = _count - 1;
    };
    _codeSet = (count ([_code] call FUNC(getSpeedDialExplosive))) == 0;
};
if (isNil QGVAR(CellphoneIEDs)) then {
    GVAR(CellphoneIEDs) = [];
};
private _count = GVAR(CellphoneIEDs) pushBack [_explosive,_code,GetNumber(ConfigFile >> "CfgMagazines" >> _magazineClass >> "ACE_Triggers" >> "Cellphone" >> "FuseTime")];
_count = _count + 1;
publicVariable QGVAR(CellphoneIEDs);

//display IED number message:
[format ["IED %1 code: %2", _count,_code]] call EFUNC(common,displayTextStructured);

if !(_hasRequired) exitWith {};
[format ["IED %1", _count],_code] call FUNC(addToSpeedDial);

/*
 * Author: Garth 'L-H' de Wet
 * Adds an explosive as a clacker item to the passed unit if the unit has the required item.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Explosive <OBJECT>
 * 2: Magazine classname <STRING>
 * 3: Extra variables <ARRAY>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, _explosive, "SatchelCharge_Remote_Mag", [ConfigFile >> "ACE_Triggers" >> "Command"]] call ACE_Explosives_fnc_addClacker;
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_unit", "_explosive", "_magazineClass"];
TRACE_3("params",_unit,_explosive,_magazineClass);

// Config is the last item in the list of passed in items.
private _config = (_this select 3) select (count (_this select 3) - 1);

private _requiredItems = getArray(_config >> "requires");
private _hasRequired = true;
private _detonators = [_unit] call FUNC(getDetonators);
{
    if !(_x in _detonators) exitWith{
        _hasRequired = false;
    };
} count _requiredItems;

if !(_hasRequired) exitWith {};
private _config = ConfigFile >> "CfgMagazines" >> _magazineClass >> "ACE_Triggers" >> configName _config;

private _clacker = _unit getVariable [QGVAR(Clackers), []];
GVAR(PlacedCount) = GVAR(PlacedCount) + 1;

_clacker pushBack [_explosive, getNumber(_config >> "FuseTime"), format [localize LSTRING(DetonateCode),
    GVAR(PlacedCount)], _magazineClass, configName ((_this select 3) select (count (_this select 3) - 1))];

_unit setVariable [QGVAR(Clackers), _clacker, true];

//display clacker code message:
[format [localize LSTRING(DetonateCode), GVAR(PlacedCount)]] call EFUNC(common,displayTextStructured);

/*
 * Author: Garth 'L-H' de Wet
 * Opens the UI for explosive detonation selection
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Trigger classname <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, "ACE_M26_Clacker"] call ACE_Explosives_fnc_addDetonateActions;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_detonator"];
TRACE_2("params",_unit,_detonator);

private _range = getNumber (ConfigFile >> "CfgWeapons" >> _detonator >> QGVAR(Range));

private _result = [_unit] call FUNC(getPlacedExplosives);
private _children = [];
private _explosivesList = [];
{
    if (!isNull(_x select 0)) then {
        private _required = getArray (ConfigFile >> "ACE_Triggers" >> (_x select 4) >> "requires");
        if (_detonator in _required) then {
            private _item = ConfigFile >> "CfgMagazines" >> (_x select 3);

            _explosivesList pushBack _x;

            _children pushBack
                [
                    [
                        format ["Explosive_%1", _forEachIndex],
                        _x select 2,
                        getText(_item >> "picture"),
                        {(_this select 2) call FUNC(detonateExplosive);},
                        {true},
                        {},
                        [_unit,_range,_x,_detonator]
                    ] call EFUNC(interact_menu,createAction),
                    [],
                    _unit
                ];
        };
    };
} forEach _result;
if (_detonator != "ACE_DeadManSwitch") then {
    // Add action to detonate all explosives tied to the detonator
    if (count _explosivesList > 0) then {
        _children pushBack [
        [
            "Explosive_All",
            localize LSTRING(DetonateAll),
            getText(ConfigFile >> "CfgWeapons" >> _detonator >> "picture"),
            {(_this select 2) call FUNC(detonateExplosiveAll);},
            {true},
            {},
            [_unit,_range,_explosivesList, _detonator]
            ] call EFUNC(interact_menu,createAction),
            [],
            _unit
        ];
    };
} else {
    //Add action to detonate all explosives (including the inventory explosive):
    _children pushBack [
    [
    "Explosive_All_Deadman",
    localize LSTRING(DetonateAll),
    getText(ConfigFile >> "CfgWeapons" >> _detonator >> "picture"),
    {[_player] call FUNC(onIncapacitated)},
    {true}
    ] call EFUNC(interact_menu,createAction),
    [],
    _unit
    ];

    //Adds actions for the explosives you can connect to the deadman switch.
    private _connectedInventoryExplosive = _unit getVariable [QGVAR(deadmanInvExplosive), ""];
    if ((_connectedInventoryExplosive != "") && {!(_connectedInventoryExplosive in (magazines _unit))}) then {
        TRACE_1("set, but missing in inventory",_connectedInventoryExplosive);
        _unit setVariable [QGVAR(deadmanInvExplosive), "", true];
    };

    _connectedInventoryExplosive = _unit getVariable [QGVAR(deadmanInvExplosive), ""];
    if (_connectedInventoryExplosive != "") then {
        //Add the disconect action
        private _magConfig = configFile >> "CfgMagazines" >> _connectedInventoryExplosive;
        private _name = if ((getText (_magConfig >> "displayNameShort")) != "") then {
            getText (_magConfig >> "displayNameShort")
        } else {
            getText(_magConfig >> "displayName")
        };
        private _picture = getText (_magConfig >> "picture");

        _children pushBack [
        ([
        "Deadman_disconnect",
        format ["%1 %2", localize "str_disp_disconnect", _name],
        _picture,
        {
            params ["_player"];
            TRACE_1("clear",_player);
            _player setVariable [QGVAR(deadmanInvExplosive), "", true];
        },
        {true}
        ] call EFUNC(interact_menu,createAction)), [], _unit];

    } else {
        //Add all magazines that would work with the deadman switch
        private _procressedMags = [];
        {
            private _mag = _x;
            if (!(_mag in _procressedMags)) then {
                _procressedMags pushBack _x;
                private _magConfig = configFile >> "CfgMagazines" >> _mag;
                private _supportedTriggers = getArray (_magConfig >> "ACE_Triggers" >> "SupportedTriggers");
                if (({_x == "DeadmanSwitch"} count _supportedTriggers) == 1) then { //case insensitive search
                    private _name = if ((getText (_magConfig >> "displayNameShort")) != "") then {
                        getText (_magConfig >> "displayNameShort")
                    } else {
                        getText(_magConfig >> "displayName")
                    };
                    private _picture = getText (_magConfig >> "picture");

                    _children pushBack [
                    ([
                    format ["Deadman_exp_%1", _mag],
                    format [localize LSTRING(connectInventoryExplosiveToDeadman), _name],
                    _picture,
                    {
                        params ["_player", "", "_mag"];
                        TRACE_2("set new",_player,_mag);
                        _player setVariable [QGVAR(deadmanInvExplosive), _mag, true];
                    },
                    {(_this select 2) in (magazines _player)},
                    {},
                    (_mag)
                    ] call EFUNC(interact_menu,createAction)), [], _unit];
                };
            };
        } forEach (magazines _unit);
    };
};

_children

/*
 * Author: Garth 'L-H' de Wet and CAA-Picard
 * Adds sub actions for all explosive magazines (from insertChildren)
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * Actions <ARRAY>
 *
 * Example:
 * [bob] call ace_explosives_fnc_addExplosiveActions
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];
TRACE_1("params",_unit);

private _mags = magazines _unit;
private _list = [];
private _itemCount = [];
{
    private _item = ConfigFile >> "CfgMagazines" >> _x;
    if (getNumber(_item >> QGVAR(Placeable)) == 1) then {
        private _index = _list find _item;
        if (_index != -1) then {
            _itemCount set [_index, (_itemCount select _index) + 1];
        } else {
            _list pushBack _item;
            _itemCount pushBack 1;
        };
    };
} forEach _mags;

private _children = [];

{
    private _name = getText (_x >> "displayNameShort");
    if (_name isEqualTo "") then {
        _name = getText (_x >> "displayName");
    };

    _children pushBack
        [
            [
                format ["Explosive_%1", _forEachIndex],
                format [_name + " (%1)", _itemCount select _forEachIndex],
                getText(_x >> "picture"),
                {[{_this call FUNC(setupExplosive)}, _this] call CBA_fnc_execNextFrame},
                {true},
                {},
                (configName _x)
            ] call EFUNC(interact_menu,createAction),
            [],
            _unit
        ];
} forEach _list;

_children

/*
 * Author: Garth 'L-H' de Wet
 * Sets the speed dial for the UI.
 *
 * Arguments:
 * 0: Name of speed dial <STRING>
 * 1: Code to add to speed dial <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * ["My Speed Dial","2131"] call ACE_explosives_fnc_addToSpeedDial;
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_name", "_code"];
TRACE_2("params",_name,_code);

private _speedDial = ace_player getVariable [QGVAR(SpeedDial), []];
private _found = false;

if ((_code) == "") exitWith {
    [_name] call FUNC(removeFromSpeedDial);
};
{
    if ((_x select 0) == _name) exitWith {
        _speedDial set [_forEachIndex, _this];
        _found = true;
    };
} forEach _speedDial;
if (!_found) then {
    _speedDial pushBack _this;
};

ace_player setVariable [QGVAR(SpeedDial), _speedDial];

/*
 * Author: Garth 'L-H' de Wet
 * Opens the UI for selecting the transmitter
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player] call ACE_Explosives_fnc_addTransmitterActions;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];
TRACE_1("params",_unit);

private _detonators = [_unit] call FUNC(getDetonators);
private _children = [];
{
    private _config = ConfigFile >> "CfgWeapons" >> _x;
    _children pushBack
        [
            [
                format ["Trigger_%1", _forEachIndex],
                getText(_config >> "displayName"),
                getText(_config >> "picture"),
                {},
                {true},
                {(_this select 2) call FUNC(addDetonateActions);},
                [_unit,_x]
            ] call EFUNC(interact_menu,createAction),
            [],
            _unit
        ];
} forEach _detonators;

_children

/*
 * Author: Garth 'L-H' de Wet
 * Opens the UI for explosive trigger selection
 *
 * Arguments:
 * 0: Explosive Magazine <STRING>
 * 1: Explosive <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [lbData [8866, lbCurSel 8866], _explosive] call ACE_Explosives_fnc_addTriggerActions;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_magazine", "_explosive"];
TRACE_2("params",_magazine,_explosive);

private _isAttached = !isNull (attachedTo _explosive);
private _detonators = [ACE_player] call FUNC(getDetonators);
private _triggerTypes = [_magazine] call FUNC(triggerType);
private _magTriggers = ConfigFile >> "CfgMagazines" >> _magazine >> "ACE_Triggers";
private _children = [];
{
    private _required = getArray (_x >> "requires");
    private _hasRequiredItems = true;
    {
        if !(_x in _detonators) exitWith {
            _hasRequiredItems = false;
        };
    } count _required;
    if (_hasRequiredItems && {(!_isAttached) || {(getNumber (_x >> "isAttachable")) == 1}}) then {
        _children pushBack
            [
                [
                    format ["Trigger_%1", _forEachIndex],
                    if(isText(_magTriggers >> configName _x >> "displayName"))then
                        {getText(_magTriggers >> configName _x >> "displayName")}
                        else{getText(_x >> "displayName")},
                    if(isText(_magTriggers >> configName _x >> "picture"))then
                        {getText(_magTriggers >> configName _x >> "picture")}
                        else{getText(_x >> "picture")},
                    {(_this select 2) call FUNC(selectTrigger);},
                    {true},
                    {},
                    [_explosive, _magazine, configName _x]
                ] call EFUNC(interact_menu,createAction),
                [],
                ACE_Player
            ];
    };
} forEach _triggerTypes;

_children

/*
 * Author: Garth 'L-H' de Wet
 * Whether a unit can perform the defuse action
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 0: Target (ACE_DefuseObject) <OBJECT>
 *
 * Return Value:
 * Able to defuse <BOOL>
 *
 * Example:
 * if ([player] call ACE_Explosives_fnc_canDefuse) then {hint "Can Defuse";};
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_unit", "_target"];
TRACE_2("params",_unit,_target);

private _explosive = _target getVariable [QGVAR(Explosive), objNull];
if (isNull _explosive) exitWith {
    deleteVehicle _target;
    false
};
if (vehicle _unit != _unit || {!("ACE_DefusalKit" in (items _unit))}) exitWith {false};

if (GVAR(RequireSpecialist) && {!([_unit] call EFUNC(Common,isEOD))}) exitWith {false};

//Handle the naval mines (which doens't get turned into items when defused):
if ((_explosive isKindOf "UnderwaterMine_Range_Ammo") && {!mineActive _explosive}) exitWith {false};

true

/*
 * Author: VKing
 * Add preplaced explosives to a unit's detonator.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Explosive object <OBJECT>
 * 2: Detonator type <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, claymore1, "ACE_Clacker"] call ace_explosives_fnc_connectExplosive
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_unit", "_object", "_detonator"];
TRACE_3("Params",_unit,_object,_detonator);

private _detonatorConfig = getText (configFile >> "CfgWeapons" >> _detonator >> QGVAR(triggerType));

private _magazineClass = getText (configFile >> "CfgAmmo" >> typeOf _object >> QGVAR(magazine));

[_unit, _object, _magazineClass, [configFile >> "ACE_Triggers" >> _detonatorConfig]] call FUNC(addClacker);

/*
 * Author: Garth 'L-H' de Wet
 * Causes the unit to defuse the passed explosive.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Explosive <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, ACE_Interaction_Target] call ACE_Explosives_fnc_defuseExplosive;
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_unit", "_explosive"];
TRACE_2("params",_unit,_explosive);

if (GVAR(ExplodeOnDefuse) && {(random 1.0) < (getNumber (ConfigFile >> "CfgAmmo" >> typeOf _explosive >> QGVAR(explodeOnDefuseChance)))}) exitWith {
    TRACE_1("exploding on defuse",_explosive);
    [_unit, -1, [_explosive, 1], "#ExplodeOnDefuse"] call FUNC(detonateExplosive);
    [QGVAR(explodeOnDefuse), [_explosive, _unit]] call CBA_fnc_globalEvent;
};

_unit action ["Deactivate", _unit, _explosive];
[QGVAR(defuse), [_explosive, _unit]] call CBA_fnc_globalEvent;

/*
 * Author: Garth 'L-H' de Wet
 * Causes the unit to detonate the passed explosive.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Max range (-1 to ignore) <NUMBER>
 * 2: Explosive <ARRAY>
 *     0: Explosive <OBJECT>
 *     1: Fuse time <NUMBER>
 * 3: Trigger Item Classname <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, 100, [Explosive, 1], "ACE_Clacker"] call ACE_Explosives_fnc_detonateExplosive; // has to be within range
 * [player, -1, [Explosive, 1], "ACE_Cellphone"] call ACE_Explosives_fnc_detonateExplosive; // range ignored.
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_unit", "_range", "_item", ["_triggerClassname", "#unknown", [""]]];
TRACE_4("detonateExplosive",_unit,_range,_item,_triggerClassname);

private _ignoreRange = (_range == -1);
if (!_ignoreRange && {(_unit distance (_item select 0)) > _range}) exitWith {TRACE_1("out of range",_range); false};

private _result = true;
{
    // Pass [Unit<OBJECT>, MaxRange <NUMBER>, Explosive <OBJECT>, FuzeTime <NUMBER>, TriggerItem <STRING>]
    private _handlerResult = [_unit, _range, _item select 0, _item select 1, _triggerClassname] call _x;
    if (_handlerResult isEqualTo false) then {TRACE_1("Handler Failed",_forEachIndex); _result = false};
} forEach GVAR(detonationHandlers);
if (!_result) exitWith {false};

if (getNumber (ConfigFile >> "CfgAmmo" >> typeOf (_item select 0) >> "TriggerWhenDestroyed") == 0) then {
    private _previousExp = _item select 0;
    private _exp = getText (ConfigFile >> "CfgAmmo" >> typeOf (_previousExp) >> QGVAR(Explosive));
    if (_exp != "") then {
        _exp = createVehicle [_exp, [0,0,15001], [], 0, "NONE"];
        _exp setDir (getDir _previousExp);
        _item set [0, _exp];
        private _pos = getPosASL _previousExp;
        deleteVehicle _previousExp;
        _exp setPosASL _pos;
    };
};

if (isNull (_item select 0)) then {
    WARNING_1("Explosive is null [%1]",_this);
};
if ((getNumber (configFile >> "CfgAmmo" >> (typeOf (_item select 0)) >> "triggerWhenDestroyed")) != 1) then {
    WARNING_1("Explosive is not triggerWhenDestroyed [%1]",typeOf (_item select 0));
};

[QGVAR(detonate), [_unit, _item select 0, _item select 1]] call CBA_fnc_serverEvent;

_result

