/*
 * Author: Ir0n1E
 * Calculate mass of weapon an items.
 *
 * Arguments:
 * 0: Weapon <STRING>
 * 1: Items <ARRAY>
 * 2: Magazines <ARRAY>
 *
 * Return Value:
 * Mass <NUMBER>
 *
 * Example:
 * ["weapon", ["item1", "item2"], ["mag1", "mag2"]] call ace_gunbag_fnc_calculateMass
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_weapon", "_items", "_magazines"];

private _mass = getNumber (configFile >> "CfgWeapons" >> _weapon >> "WeaponSlotsInfo" >> "mass");

{
    _mass = _mass + getNumber (configFile >> "CfgWeapons" >> _x >> "ItemInfo" >> "mass");
} foreach _items;

{
    _mass = _mass + getNumber (configFile >> "CfgMagazines" >> _x >> "mass");
} forEach _magazines;

_mass

/*
 * Author: Ir0n1E
 * Check if client able to interact with gunbag.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * -1: can't interact 0: empty gunbag 1: full gunbag <NUMBER>
 *
 * Example:
 * _canInteract = [player, target] call ace_gunbag_fnc_canInteract
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_target"];

private _result = -1;
private _gunbag = backpackContainer _target;
private _weapon = primaryWeapon _unit;

if ((_gunbag getVariable [QGVAR(gunbagWeapon), []]) isEqualTo [] && {_weapon != ""} && {!(_weapon call FUNC(isMachineGun))}) then {
    _result = 0;
};

if (!((_gunbag getVariable [QGVAR(gunbagWeapon), []]) isEqualTo []) && {_weapon == ""}) then {
    _result = 1;
};

_result

/*
 * Author: commy2
 * Reports true if a weapon is a machine gun.
 *
 * Arguments:
 * 0: Weapon class name <STRING>
 *
 * Return Value:
 * Is machine gun <BOOL>
 *
 * Example:
 * (currentWeapon player) call ace_gunbag_fnc_isMachineGun
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_weapon"];

private _config = _weapon call CBA_fnc_getItemConfig;

// definition of a machine gun by BIS_fnc_itemType
private _cursor = getText (_config >> "cursor");

if (toLower _cursor in ["", "emptycursor"]) then {
    _cursor = getText (_config >> "cursorAim");
};

_cursor == "MG"

/*
 * Author: Ir0n1E
 * Get weapon out of gunbag.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, target] call ace_gunbag_fnc_toGunbag
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_target"];

private _gunbag = backpackContainer _target;

_unit call EFUNC(common,goKneeling);

// play sound
if (["ace_backpacks"] call EFUNC(common,isModLoaded)) then {
    [_target, _gunbag] call EFUNC(backpacks,backpackOpened);
};

[PROGRESSBAR_TIME, _this, {
    (_this select 0) call FUNC(offGunbagCallback)
}, {}, localize LSTRING(offGunbag), {
    (_this select 0) call FUNC(canInteract) == 1
}] call EFUNC(common,progressBar);

/*
 * Author: Ir0n1E
 * Get weapon out of gunbag.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, target] call ace_gunbag_fnc_offGunbagCallback
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_target"];

private _gunbag = backpackContainer _target;

private _state = _gunbag getVariable [QGVAR(gunbagWeapon), []];

if (_state isEqualTo []) exitWith {
    [localize LSTRING(empty)] call EFUNC(common,displayTextStructured);
};

_state params ["_weapon", "_items", "_magazines"];

_unit addWeapon _weapon;

// Game will auto add magazines from player's inventory, put these back in player inventory as they will be overwritten
([_unit, _weapon] call EFUNC(common,getWeaponState)) params ["", "", "_addedMags", "_addedAmmo"];
{
    if (((_x select 0) != "") && {(_addedMags select _forEachIndex) != ""}) then {
        TRACE_2("Re-adding mag",_x,_addedMags select _forEachIndex);
        _unit addMagazine [_addedMags select _forEachIndex, _addedAmmo select _forEachIndex];
    };
} forEach _magazines;

removeAllPrimaryWeaponItems _unit;

{
    _unit addWeaponItem [_weapon, _x];
} forEach (_items + _magazines);

_unit selectWeapon _weapon;

_magazines = _magazines apply {_x select 0};

private _mass = [_weapon, _items, _magazines] call FUNC(calculateMass);

// remove virtual load
[_target, _gunbag, -_mass] call EFUNC(movement,addLoadToUnitContainer);
_gunbag setVariable [QGVAR(gunbagWeapon), [], true];

// play sound
if (["ace_backpacks"] call EFUNC(common,isModLoaded)) then {
    [_target, _gunbag] call EFUNC(backpacks,backpackOpened);
};

/*
 * Author: Ir0n1E
 * Check gunbag status full/empty.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player] call ace_gunbag_fnc_status
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];

private _state = (backpackContainer _unit) getVariable [QGVAR(gunbagWeapon), []];

if (_state isEqualTo []) then {
    [localize LSTRING(empty)] call EFUNC(common,displayTextStructured);
} else {
    _state params ["_weapon"];

    [
        getText (configFile >> "CfgWeapons" >> _weapon >> "displayname"),
        getText (configFile >> "CfgWeapons" >> _weapon >> "picture")
    ] call EFUNC(common,displayTextPicture);
};

/*
 * Author: Ir0n1E
 * Put weapon into gunbag.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, target] call ace_gunbag_fnc_toGunbag
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_target"];

private _gunbag = backpackContainer _target;

_unit call EFUNC(common,goKneeling);

// play sound
if (["ace_backpacks"] call EFUNC(common,isModLoaded)) then {
    [_target, _gunbag] call EFUNC(backpacks,backpackOpened);
};

[PROGRESSBAR_TIME, _this, {
    (_this select 0) call FUNC(toGunbagCallback)
}, {}, localize LSTRING(toGunbag), {
    (_this select 0) call FUNC(canInteract) == 0
}] call EFUNC(common,progressBar);

/*
 * Author: Ir0n1E
 * Put weapon into gunbag.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, target] call ace_gunbag_fnc_toGunbagCallback
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_target"];

private _weapon = primaryWeapon _unit;
private _gunbag = backpackContainer _target;

private _state = [_unit, _weapon] call EFUNC(common,getWeaponState);

/*
 * example return value _state
 * [["","","optic_Aco",""],["arifle_MX_GL_ACO_F","GL_3GL_F"],["30Rnd_65x39_caseless_mag","1Rnd_HE_Grenade_shell"],[30,1]]
 */

_state params ["_items", "", "_magazines", "_ammo"];

private _mass = [_weapon, _items, _magazines] call FUNC(calculateMass);

{
    _magazines set [_forEachIndex, [_x, _ammo select _forEachIndex]];
} forEach _magazines;

_unit removeWeapon _weapon;

// add virtual load
[_target, _gunbag, _mass] call EFUNC(movement,addLoadToUnitContainer);
_gunbag setVariable [QGVAR(gunbagWeapon), [_weapon, _items, _magazines], true];

// play sound
if (["ace_backpacks"] call EFUNC(common,isModLoaded)) then {
    [_target, _gunbag] call EFUNC(backpacks,backpackOpened);
};

