params ["_interactionTarget"];

if (dialog || {isNull _interactionTarget}) exitWith {
    disableSerialization;

    private _display = uiNamespace getVariable QGVAR(medicalMenu);
    if (!isNil "_display") then {
        closeDialog 314412;
    };
};

