/*
 * Author: PabstMirror
 * Handles the keypad entries from the "Mark" Application
 *
 * Arguments:
 * 0: String version of Keypad entry ["ok","del","1",...] <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * ["ok"] call ace_microdagr_fnc_appMarkKeypadEntry
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_keypadButton"];

disableSerialization;
private _display = uiNamespace getVariable [[QGVAR(RscTitleDisplay), QGVAR(DialogDisplay)] select (GVAR(currentShowMode) == DISPLAY_MODE_DIALOG), displayNull];

if (isNull _display) exitWith {ERROR("No Display");};

if (GVAR(currentApplicationPage) != APP_MODE_MARK) exitWith {};

private _editText = ctrlText (_display displayCtrl IDC_MODEMARK_CORDSEDIT);

switch (_keypadButton) do {
case ("ok"): {
        if ((count GVAR(newWaypointPosition)) == 0) then {
            private _actualPos = [_editText, true] call EFUNC(common,getMapPosFromGrid);
            _actualPos set [2, (getTerrainHeightASL _actualPos)];
            GVAR(newWaypointPosition) = _actualPos;
            [APP_MODE_MARK] call FUNC(saveCurrentAndSetNewMode);
        } else {
            [_editText, GVAR(newWaypointPosition)] call FUNC(deviceAddWaypoint);
            [APP_MODE_WAYPOINTS] call FUNC(saveCurrentAndSetNewMode);
        };
    };
case ("del"): {
        _editText = _editText select [0, (((count _editText) - 1) max 0)];
        (_display displayCtrl IDC_MODEMARK_CORDSEDIT) ctrlSetText _editText;
        ctrlSetFocus (_display displayCtrl IDC_MODEMARK_CORDSEDIT);
    };
    default {
        _editText = _editText + _keypadButton;
        (_display displayCtrl IDC_MODEMARK_CORDSEDIT) ctrlSetText _editText;
        ctrlSetFocus (_display displayCtrl IDC_MODEMARK_CORDSEDIT);
    };
};

/*
 * Author: PabstMirror
 * Handles double clicking on the setting listbox
 *
 * Arguments:
 * 0: Setting List box (not used) <CONTROL>
 * 1: Index <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [settingList, 1] call ace_microdagr_fnc_appSettingsLBClick
 *
 * Public: No
 */
#include "script_component.hpp"

disableSerialization;
params ["", "_itemClicked"];

switch (_itemClicked) do {
    case (0): { GVAR(settingUseMils) = ! GVAR(settingUseMils)};
    case (1): { GVAR(settingShowAllWaypointsOnMap) = ! GVAR(settingShowAllWaypointsOnMap)};
};

[] call FUNC(updateDisplay);

/*
 * Author: PabstMirror
 * Handles clicking the delete button from the waypoint application
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * call ace_microdagr_fnc_appWaypointsButtonDeleteWP
 *
 * Public: No
 */
#include "script_component.hpp"

disableSerialization;
private _display = uiNamespace getVariable [[QGVAR(RscTitleDisplay), QGVAR(DialogDisplay)] select (GVAR(currentShowMode) == DISPLAY_MODE_DIALOG), displayNull];

if (isNull _display) exitWith {ERROR("No Display");};

private _wpIndex = lbCurSel (_display displayCtrl IDC_MODEWAYPOINTS_LISTOFWAYPOINTS);

//If it's our currentWP then deactivate
if (GVAR(currentWaypoint) == _wpIndex) then {GVAR(currentWaypoint) = -1};

//Delete from list:
[_wpIndex] call FUNC(deviceDeleteWaypoint);
//Update list now:
[] call FUNC(updateDisplay);

/*
 * Author: PabstMirror
 * Handles clicking the setWP button from the waypoint application
 *
 * Arguments:
 * The "SetWP" button <CONTROL>
 *
 * Return Value:
 * None
 *
 * Example:
 * [1234] call ace_microdagr_fnc_appWaypointsButtonSetWP
 *
 * Public: No
 */
#include "script_component.hpp"

disableSerialization;
params ["_wpButton"];

private _wpListBox = (ctrlParent _wpButton) displayCtrl 144501;
private _newWpIndex = lbCurSel _wpListBox;
private _waypoints = [] call FUNC(deviceGetWaypoints);

if ((_newWpIndex < 0) || (_newWpIndex > ((count _waypoints) - 1))) exitWith {
    GVAR(currentWaypoint) = -1;
};

GVAR(currentWaypoint) = _newWpIndex;
[APP_MODE_COMPASS] call FUNC(saveCurrentAndSetNewMode);

/*
 * Author: PabstMirror
 * Tests if the dagr can be shown in a mode
 *
 * Arguments:
 * The display mode to test showing <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [1] call ace_microdagr_fnc_canShow
 *
 * Public: No
 */
#include "script_component.hpp"
params ["_showType"];

private _returnValue = false;

_returnValue = switch (_showType) do {
    case (DISPLAY_MODE_CLOSED): { true }; //Can always close
    case (DISPLAY_MODE_HIDDEN): { true }; //Can always hide
    case (DISPLAY_MODE_DIALOG): {
         ("ACE_microDAGR" in (items ACE_player)) && {[ACE_player, objNull, ["notOnMap", "isNotInside", "isNotSitting"]] call EFUNC(common,canInteractWith)}
    };
    case (DISPLAY_MODE_DISPLAY): {
        //Can't have minimap up while zoomed in on foot, but allow drivers to use while in "Gunner" to handle non-3d vehicles like most tanks
        ((cameraView != "GUNNER") || {(vehicle ACE_player != ACE_player) && {driver vehicle ACE_player == ACE_player}}) &&
        {"ACE_microDAGR" in (items ACE_player)} && 
        {[ACE_player, objNull, ["notOnMap", "isNotInside", "isNotSitting"]] call EFUNC(common,canInteractWith)}
    };
    default { false };
};

_returnValue

