/*
 * Author: Grey
 * Checks whether magazine can be loaded into static weapon
 *
 * Arguments:
 * 0: static <OBJECT>
 * 1: unit <OBJECT>
 * 2: magazine class to check; if not given having any compatible magazine returns true <STRING> (default: "")
 *
 * Return Value:
 * canLoadMagazine <BOOL>
 *
 * Example:
 * [_target,_player,"ACE_1Rnd_82mm_Mo_HE"] call ace_mk6mortar_fnc_canLoadMagazine
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit",["_magazineClassOptional","",[""]]];

if !(alive _static && GVAR(useAmmoHandling)) exitWith {false};

private _canLoadMagazine = false;
private _hasCompatibleMagazine = false;

private _currentMagazine = (magazinesAllTurrets _static) select 1;
private _weapon = (_static weaponsTurret [0]) select 0;

private _listOfMagNames = getArray(configFile >> "cfgWeapons" >> _weapon >> "magazines");
private _count = 0;

//If function is called with an optional string then check if player has that magzine otherwise check all magazines of the player to see if they are compatible with the static weapon
if (_magazineClassOptional != "") then {
    if ([_unit,_magazineClassOptional] call EFUNC(common,hasMagazine)) then {
        _hasCompatibleMagazine = true;
    };
 } else {
    {
        if ([_unit,_x] call EFUNC(common,hasMagazine)) exitWith {_hasCompatibleMagazine = true};
    } forEach _listOfMagNames;
};
//If static weapon has a magazine then find the ammo count
if (count (_static magazinesTurret [0]) > 0) then {
    _count = _currentMagazine select 2;
};
//If the static weapon doesn't have a magzine or a magazine with no bullets, the player has a compatible magazine and the static weapon has a barrel then you can load a magazine
if ((count (_static magazinesTurret [0]) == 0 || _count == 0 ) && _hasCompatibleMagazine) then {
    _canLoadMagazine = true;
};
_canLoadMagazine

/*
 * Author: Grey
 * Checks whether magazine can be unloaded from static weapon
 *
 * Arguments:
 * 0: static <OBJECT>
 * 1: unit <OBJECT>
 *
 * Return Value:
 * canUnloadMagazine <BOOL>
 *
 * Example:
 * [_target,_player] call ace_mk6mortar_fnc_canUnloadMagazine
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit"];

if !(alive _static && GVAR(useAmmoHandling) && _static getVariable [QGVAR(initialized),false]) exitWith {false};
private _canUnloadMagazine = false;

private _ammoCount = ((magazinesAllTurrets _static) select 1) select 2;
if (_ammoCount > 0) then {
    _canUnloadMagazine = true;
};
_canUnloadMagazine

/*
 * Author: PabstMirror
 * DEV function to build mortar tables, very cpu intensive (never used durring normal gameplay)
 *
 * Arguments:
 * 0: Muzzle Velocity <NUMBER>
 * 1: Air Friction <NUMBER>
 *
 * Return Value:
 * None <Data in clipboard>
 *
 * Example:
 * [100, -0.0001] spawn ace_mk6mortar_fnc_dev_buildTable; //spawn (scheduled) is slower
 * [100, -0.0001] call ace_mk6mortar_fnc_dev_buildTable; //faster, but will lock while processing
 *
 * Public: No
 */
#include "script_component.hpp"

private _muzzleVelocity = _this select 0;
private _airFriction = _this select 1;
private _stillInRange = true;
private _currentRange = 100;
private _increasePerRow = 50;
private _outputArray = [];


//[_rangeToHit, _lineElevation, _lineHeightElevation, _lineHeightTimeDelta, _lineTimeOfFlight, _lineCrosswindDeg, _lineHeadwindMeters, _lineTailWindMeters, _lineTempDec, _lineTempInc, _lineAirDensDec, _lineAirDensInc]

while {_stillInRange} do {
    private _result = [_muzzleVelocity, _currentRange, _airFriction] call FUNC(dev_simulateCalcRangeTableLine);
    if (_result isEqualTo []) then {
        _stillInRange = false;
    } else {
        if (_airFriction == 0) then {
            _result set [5, 0];
            _result set [6, 0];
            _result set [7, 0];
            _result set [8, 0];
            _result set [9, 0];
            _result set [10, 0];
            _result set [11, 0];
        };
        if ((_result select 1) < 88) then {
            _outputArray pushBack [
            ([(_result select 0),  "meters", false] call FUNC(dev_formatNumber)),
            ([(_result select 1),  "mil", true] call FUNC(dev_formatNumber)),
            ([(_result select 2),  "mil", true] call FUNC(dev_formatNumber)),
            ([(_result select 3),  "sec", false] call FUNC(dev_formatNumber)),
            ([(_result select 4),  "sec", false] call FUNC(dev_formatNumber)),
            ([(_result select 5),  "milPrecise", true] call FUNC(dev_formatNumber)),
            ([(_result select 6),  "metersprecise", false] call FUNC(dev_formatNumber)),
            ([(_result select 7),  "metersprecise", false] call FUNC(dev_formatNumber)),
            ([(_result select 8),  "metersprecise", false] call FUNC(dev_formatNumber)),
            ([(_result select 9),  "metersprecise", false] call FUNC(dev_formatNumber)),
            ([(_result select 10),  "metersprecise", false] call FUNC(dev_formatNumber)),
            ([(_result select 11), "metersprecise", false] call FUNC(dev_formatNumber))
            ];
        };
        _currentRange = _currentRange + _increasePerRow;
    };
    hintSilent str _currentRange;
};

//handle floating point rounding errors
private _outputString = format ["case ((abs(_muzzleVelocity - %1) < 0.00001) && {(abs(_airFriction - %2) < 0.00001)}): {
[
", _muzzleVelocity, _airFriction];

{
    if (_forEachIndex < ((count _outputArray) - 1)) then {
        _outputString = _outputString + format ["%1,
    ", _x];
    } else {
        _outputString = _outputString + format ["%1
    ]
    };", _x];
    };
} forEach _outputArray;

copyToClipboard _outputString;
rangeTableOutput = _outputString;

hint "done";

/*
 * Author: Pabst Mirror
 * Converts numbers into nicely formated strings.
 *
 * Arguments:
 * 0: Input number <NUMBER>
 * 1: Output type (see case statement) <STRING>
 * 2: If output type is mil, convert input type from deg->mil <BOOL>
 *
 * Return Value:
 * Formatted number <STRING>
 *
 * Example:
 * [45, "mil4", true] call ace_mk6mortar_fnc_dev_formatNumber = "0800"
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_theNumber", "_inputType", "_convertToMils"];

private _decimalPlaces = -1;
private _integerPlaces = -1;

switch (toLower _inputType) do {
case ("meters"): {
        _decimalPlaces = 0;
        _integerPlaces = 1;
    };
case ("metersprecise"): {
        _decimalPlaces = 1;
        _integerPlaces = 1;
    };
case ("meters4"): {
        _decimalPlaces = 0;
        _integerPlaces = 4;
    };
case ("deg3precise"): {
        _decimalPlaces = 2;
        _integerPlaces = 3;
    };
case ("mil"): {
        _decimalPlaces = 0;
        _integerPlaces = 1;
        if (_convertToMils) then {
            _theNumber = _theNumber * (6400 / 360);
        };
    };
case ("mil4"): {
        _decimalPlaces = 0;
        _integerPlaces = 4;
        if (_convertToMils) then {
            _theNumber = _theNumber * (6400 / 360);
        };
    };
case ("milprecise"): {
        _decimalPlaces = 1;
        _integerPlaces = 1;
        if (_convertToMils) then {
            _theNumber = _theNumber * (6400 / 360);
        };
    };
case ("sec"): {
        _decimalPlaces = 1;
        _integerPlaces = 1;
    };
    default {systemChat format ["badtype %1", _inputType];};
};

//CBA_fnc_formatNumber is silly:  [-9.58545, 1, 1, false] call CBA_fnc_formatNumber == "-9.-6"

private _prefix = if (_theNumber < 0) then {"-"} else {""};

private _return = [abs (_theNumber), _integerPlaces, _decimalPlaces, false] call CBA_fnc_formatNumber;

(_prefix + _return)

/*
 * Author: Pabst Mirror
 * Builds a rangeTable line for a certian range, given muzzle velocity and air friction, returns [] if out of range.
 *
 * Arguments:
 * 0: Muzzle Velocity <NUMBER>
 * 1: Air Friction <NUMBER>
 * 2: Range To Hit <NUMBER>
 *
 * Return Value:
 * Range Table Line Data (see return line) <ARRAY>
 *
 * Example:
 * [300, -0.0001, 3000] call ace_mk6mortar_fnc_simulateCalcRangeTableLine
 *
 * Public: No
 */
#include "script_component.hpp"

#define TIME_STEP (1/50)

params ["_muzzleVelocity", "_rangeToHit", "_airFriction"];

private _startTime = diag_tickTime;



//Run Binary search for correct elevation
private _solution = [_rangeToHit, 0, _muzzleVelocity, _airFriction, TIME_STEP] call FUNC(dev_simulateFindSolution);
if (_solution isEqualTo []) exitWith {[]};

//Real Elevation
private _lineElevation = _solution select 0;

//Time Of Flight:
private _lineTimeOfFlight = _solution select 1;

//Height Adjustment for -100m (another binary search)
private _solution = [_rangeToHit, -100, _muzzleVelocity, _airFriction, TIME_STEP] call FUNC(dev_simulateFindSolution);
if (_solution isEqualTo []) exitWith {[]};//should never be triggered (lower elevation easier to hit)

private _lineHeightElevation = ((_solution select 0) - _lineElevation);
private _lineHeightTimeDelta = (_solution select 1) - _lineTimeOfFlight;

//Compute for 10x and divide to minimize rounding errors

//Crosswind
private _lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, 15, 1, 0, 10, 0, TIME_STEP] call FUNC(dev_simulateShot);
private _lineCrosswindDeg = (_lastTestResult select 2) / 10;

//Headwind:
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, 15, 1, -10, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
private _lineHeadwindMeters = (_rangeToHit - (_lastTestResult select 0)) / 10;

//TailWind:
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, 15, 1, 10, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
private _lineTailWindMeters = (_rangeToHit - (_lastTestResult select 0)) / 10;

//Air Temp Dec
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, (15 - 10), 1, 0, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
_lineTempDec = (_rangeToHit - (_lastTestResult select 0)) / 10;

//Air Temp Inc
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, (15 + 10), 1, 0, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
_lineTempInc = (_rangeToHit - (_lastTestResult select 0)) / 10;

//Air Density Dec
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, 15, 0.9, 0, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
private _lineAirDensDec = (_rangeToHit - (_lastTestResult select 0)) / 10;

//Air Density Inc
_lastTestResult = [_lineElevation, _muzzleVelocity, _airFriction, 15, 1.1, 0, 0, 0, TIME_STEP] call FUNC(dev_simulateShot);
private _lineAirDensInc = (_rangeToHit - (_lastTestResult select 0)) / 10;

// systemChat format ["debug: Range %1 - in %2 sec", _rangeToHit, (diag_tickTime - _startTime)];

[_rangeToHit, _lineElevation, _lineHeightElevation, _lineHeightTimeDelta, _lineTimeOfFlight, _lineCrosswindDeg, _lineHeadwindMeters, _lineTailWindMeters, _lineTempDec, _lineTempInc, _lineAirDensDec, _lineAirDensInc]

/*
 * Author: PabstMirror
 * DEV to find a firing solution for a given range
 *
 * Arguments:
 * 0: Range to Hit (Meters) <NUMBER>
 * 1: Height To Hit (Meters) <NUMBER>
 * 2: Muzzle Velocity (M/S) <NUMBER>
 * 3: Air Friction <NUMBER>
 * 4: Time Step (seconds) (eg 1/50 will simulate 50 cycles per second) <NUMBER>
 *
 * Return Value:
 * [NUMBER - Elevation In Degrees, NUMBER - Shot Durration] <ARRAY>
 *
 * Example:
 * [_rangeToHit, _heightToHit, _muzzleVelocity, _airFriction, TIME_STEP] call ace_mk6mortar_fnc_dev_simulateFindSolution;
 *
 * Public: No
 */
#include "script_component.hpp"

#define MAX_ATTEMPTS 22
params ["_rangeToHit", "_heightToHit", "_muzzleVelocity", "_airFriction","_timeStep"];

private _maxElev = 90;
private _minElev = 45; //todo - Low Angle Howitzers???

private _error = 10000;
private _solutionElevation = -1;
private _lastTestResult = [];
private _numberOfAttempts = 0;

//(binary search)
while {(_numberOfAttempts < MAX_ATTEMPTS) && {(abs _error) > 0.2}} do {
    _numberOfAttempts = _numberOfAttempts + 1;
    _solutionElevation = (_maxElev + _minElev) / 2;
    _lastTestResult = [_solutionElevation, _muzzleVelocity, _airFriction, 15, 1, 0, 0, _heightToHit, _timeStep] call FUNC(dev_simulateShot);
    _error = _rangeToHit - (_lastTestResult select 0);
    if (_error > 0) then {
        _maxElev = _solutionElevation; //test range was short
    } else {
        _minElev = _solutionElevation; //test range was long
    };
};
if (_numberOfAttempts >= MAX_ATTEMPTS) exitWith {[]};

//return the elevation and time required
[_solutionElevation, (_lastTestResult select 1)]

/*
 * Author: PabstMirror
 * DEV function to build mortar tables, very cpu intensive (never used durring normal gameplay)
 *
 * Arguments:
 * 0: Shot Angle (degrees) <NUMBER>
 * 1: Muzzle Velocity (m/s) <NUMBER>
 * 2: Air Friction <NUMBER>
 * 3: Tempeture (degres celcius) <NUMBER>
 * 4: Relative Air Denisty <NUMBER>
 * 5: Tail Wind (m/s) <NUMBER>
 * 6: Cross Wind (m/s) <NUMBER>
 * 7: Height Of Target (M) <NUMBER>
 * 8: Time Step (fraction of a second) <NUMBER>
 *
 * Return Value:
 * <ARRAY> [Distance Traveled<NUMBER>, Shot Time<NUMBER>, Offset (degrees)<NUMBER>]
 *
 * Example:
 * [45, 180, -0.0001, 15, 1, 10, 0, 0, 1/50] call ace_mk6mortar_fnc_dev_simulateShot;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_angleDeg", "_muzzleVelocity", "_airFriction", "_temp", "_relDensity", "_tailWind", "_crosswind", "_heightOfTarget", "_timeStep"];

private _wind = [_crosswind, _tailWind, 0];
private _gravity = [0,0,-9.8];

private _currentPos = [0,0,0];
private _muzzleVelocity = _muzzleVelocity * (((_temp + 273.13) / 288.13 - 1) / 40 + 1);
private _currentVelocity = [0, (_muzzleVelocity * cos _angleDeg), (_muzzleVelocity * sin _angleDeg)];

private _currentTime = 0;
private _lastPos = _currentPos;

private _kCoefficent = -1 * _relDensity * _airFriction;  //save time in the loop and compute once

while {((_currentVelocity select 2) > 0) || ((_currentPos select 2) >= _heightOfTarget)} do {
    _lastPos = _currentPos;

    private _aparentWind = _wind vectorDiff _currentVelocity;
    private _changeInVelocity = _gravity vectorAdd (_aparentWind vectorMultiply ((vectorMagnitude _aparentWind) * _kCoefficent));

    _currentVelocity = _currentVelocity vectorAdd (_changeInVelocity vectorMultiply _timeStep);

    _currentPos = _currentPos vectorAdd (_currentVelocity vectorMultiply _timeStep);
    _currentTime = _currentTime + _timeStep;
};

//Uses linearConversion to get a weighted average betwen points before and after dropping below target height
private _linConversion = linearConversion [(_lastPos select 2), (_currentPos select 2), _heightOfTarget, 0, 1, true];
private _middlePos = (_lastPos vectorMultiply (1 - _linConversion)) vectorAdd (_currentPos vectorMultiply (_linConversion));
// private _middlePosOld = (_lastPos vectorAdd _currentPos) vectorMultiply 0.5;

//Same to find travel time
private _middleTotalTravelTime = _currentTime - (_timeStep * (1-_linConversion));

//Find shot offset (from crosswind), in degrees
private _offsetDeg = (_middlePos select 0) aTan2 (_middlePos select 1);

[(_middlePos select 1), _middleTotalTravelTime, _offsetDeg]

/*
 * Author: PabstMirror
 * Called when the mortar is fired.
 *
 * Arguments:
 * 0: mortar - Object the event handler is assigned to <OBJECT>
 * 1: weapon - Fired weapon <STRING>
 * 2: muzzle - Muzzle that was used <STRING>
 * 3: mode - Current mode of the fired weapon <STRING>
 * 4: ammo - Ammo used <STRING>
 * 5: magazine - magazine name which was used <STRING>
 * 6: projectile - Object of the projectile that was shot <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [clientFiredBIS-XEH] call ace_mk6mortar_fnc_handleFired
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_vehicle", "_weapon", "_muzzle", "_mode", "_ammo", "_magazine", "_projectile"];

if (GVAR(useAmmoHandling) && {_vehicle getVariable [QGVAR(initialized),false] && !(_vehicle getVariable [QGVAR(exclude),false])}) then {
    // if !(_vehicle getVariable [QGVAR(exclude),false]) then {
        _vehicle removeMagazineGlobal (_vehicle magazinesTurret [0] select 0);
        TRACE_1("",_vehicle magazinesTurret [0]);
    // };
};

if (!GVAR(airResistanceEnabled)) exitWith {};

// Large enough distance to not simulate any wind deflection
if (_vehicle distance ACE_player > 8000) exitWith {false};

//AI will have no clue how to use:
_shooterMan = gunner _vehicle;
if (!([_shooterMan] call EFUNC(common,isPlayer))) exitWith {false};

//Calculate air density:
private _altitude = (getPosASL _vehicle) select 2;
private _temperature = _altitude call EFUNC(weather,calculateTemperatureAtHeight);
private _pressure = _altitude call EFUNC(weather,calculateBarometricPressure);
private _relativeHumidity = EGVAR(weather,currentHumidity);
private _airDensity = [_temperature, _pressure, _relativeHumidity] call EFUNC(weather,calculateAirDensity);
private _relativeDensity = _airDensity / 1.225;

TRACE_5("FiredWeather",_temperature,_pressure,_relativeHumidity,_airDensity,_relativeDensity);

//powder effects:
private _newMuzzleVelocityCoefficent = (((_temperature + 273.13) / 288.13 - 1) / 40 + 1);
if (_newMuzzleVelocityCoefficent != 1) then {
    _bulletVelocity = velocity _projectile;
    _bulletSpeed = vectorMagnitude _bulletVelocity;
    _bulletVelocity = (vectorNormalized _bulletVelocity) vectorMultiply (_bulletSpeed * _newMuzzleVelocityCoefficent);
    _projectile setVelocity _bulletVelocity;
};


[{
    params ["_args", "_pfID"];
    _args params ["_shell", "_airFriction", "_time", "_relativeDensity"];

    if (isNull _shell || {!alive _shell}) exitWith {
        [_pfID] call CBA_fnc_removePerFrameHandler;
    };

    private _deltaT = CBA_missionTime - _time;
    _args set[2, CBA_missionTime];

    private _bulletVelocity = velocity _shell;
    private _bulletSpeed = vectorMagnitude _bulletVelocity;

    private _trueVelocity = _bulletVelocity vectorDiff wind;
    private _trueSpeed = vectorMagnitude _trueVelocity;

    private _drag = _deltaT * _airFriction * _trueSpeed * _relativeDensity;
    private _accel = _trueVelocity vectorMultiply (_drag);
    private _bulletVelocity = _bulletVelocity vectorAdd _accel;

    _shell setVelocity _bulletVelocity;

}, 0, [_projectile, MK6_82mm_AIR_FRICTION, CBA_missionTime, _relativeDensity]] call CBA_fnc_addPerFrameHandler;

/*
 * Author: PabstMirror
 * Handles player getting into new vehicle.  Loads PFEG for mortar display if it is a mortar.
 *
 * Arguments:
 * 0: Player <OBJECT>
 * 1: New Vehicle <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [bob, mortar] call ace_mk6mortar_fnc_handlePlayerVehicleChanged;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_player", "_newVehicle"];

if (isNull _newVehicle) exitWith {};
if (!(_newVehicle isKindOf "Mortar_01_base_F")) exitWith {};

// Run magazine handling initialization if enabled
if (!(_newVehicle getVariable [QGVAR(initialized),false]) && !(_newVehicle getVariable [QGVAR(exclude),false])) then {
    // Make sure that mortar init is executed after settings init
    [{
        params ["_mortar"];
        if (GVAR(useAmmoHandling) && {!(_mortar getVariable [QGVAR(initialized),false]) && !(_mortar getVariable [QGVAR(exclude),false])}) then {
            //wait for proper turret locality change
            [{
                ["ace_initMortar", [_this], [_this]] call CBA_fnc_globalEvent;
            }, _mortar, 0.05] call CBA_fnc_waitAndExecute;
        };
    }, _newVehicle] call EFUNC(common,runAfterSettingsInit);
};

private _tubeWeaponName = (weapons _newVehicle) select 0;
private _fireModes = getArray (configFile >> "CfgWeapons" >> _tubeWeaponName >> "modes");

//Restore last firemode:
private _lastFireMode = _newVehicle getVariable [QGVAR(lastFireMode), -1];
if (_lastFireMode != -1) then {
    _player action ["SwitchWeapon", _newVehicle, _player, _lastFireMode];
};

[{
    params ["_args", "_pfID"];
    _args params ["_mortarVeh", "_fireModes"];

    if ((vehicle ACE_player) != _mortarVeh) then {
        [_pfID] call CBA_fnc_removePerFrameHandler;
    } else {

        private _useMils = _mortarVeh getVariable [QGVAR(useMils), true];

        //Compute: 'charge' from weaponstate
        private _currentFireMode = (weaponState [_mortarVeh, [0]]) select 2;
        private _currentChargeMode = _fireModes find _currentFireMode;

        //Save firemode on vehicle:
        _mortarVeh setVariable [QGVAR(lastFireMode), _currentChargeMode];

        if (shownArtilleryComputer && {!GVAR(allowComputerRangefinder)}) then {
            //Don't like this solution, but it works
            closeDialog 0;
            [parseText "Computer Disabled"] call EFUNC(common,displayTextStructured);
        };

        private _display = uiNamespace getVariable ["ACE_Mk6_RscWeaponRangeArtillery", displayNull];
        if (isNull _display) exitWith {}; //It may be null for the first frame

        private _chargeText = format ["<t size='0.8'>%1: %2 <img image='%3'/></t>", (localize LSTRING(rangetable_charge)), _currentChargeMode, QPATHTOF(UI\ui_charges.paa)];

        //Hud should hidden in 3rd person
        private _notGunnerView = cameraView != "GUNNER";

        //Calc real azimuth/elevation
        //(looking at the sky VS looking at ground will radicaly change fire direction because BIS)
        private _realAzimuth = -1;
        private _realElevation = -1;

        private _useRealWeaponDir = (ctrlText (_display displayCtrl 173)) == "--";
        if (_useRealWeaponDir && {(_mortarVeh ammo (currentWeapon _mortarVeh)) == 0}) then {
            // With no ammo, distance display will be empty, but gun will still fire at wonky angle if aimed at ground
            private _testSeekerPosASL = AGLtoASL (positionCameraToWorld [0,0,0]);
            private _testSeekerDir = _testSeekerPosASL vectorFromTo (AGLtoASL (positionCameraToWorld [0,0,1]));
            private _testPoint = _testSeekerPosASL vectorAdd (_testSeekerDir vectorMultiply viewDistance);
            if ((terrainIntersectASL [_testSeekerPosASL, _testPoint]) || {lineIntersects [_testSeekerPosASL, _testPoint]}) then {
                _useRealWeaponDir = false; // If we are not looking at infinity (based on viewDistance)
            };
        };

        if (_useRealWeaponDir) then {
            //No range (looking at sky), it will follow weaponDir:
            private _weaponDir = _mortarVeh weaponDirection (currentWeapon _mortarVeh);
            _realAzimuth = (_weaponDir select 0) atan2 (_weaponDir select 1);
            _realElevation = asin (_weaponDir select 2);
        } else {
            //Valid range, will fire at camera dir
            private _lookVector = ((positionCameraToWorld [0,0,0]) call EFUNC(common,positionToASL)) vectorFromTo ((positionCameraToWorld [0,0,10]) call EFUNC(common,positionToASL));
            _realAzimuth = ((_lookVector select 0) atan2 (_lookVector select 1));
            private _upVectorDir = (((vectorUp _mortarVeh) select 0) atan2 ((vectorUp _mortarVeh) select 1));
            private _elevationDiff = (cos (_realAzimuth - _upVectorDir)) * acos ((vectorUp _mortarVeh) select 2);
            _realElevation = ((180 / PI) * (_mortarVeh animationPhase "mainGun")) + 75 - _elevationDiff;
        };

        //Update Heading Display:
        if (_notGunnerView || (!GVAR(allowCompass))) then {
            (_display displayCtrl 80156) ctrlSetText "";
        } else {
            if (_useMils) then {
                (_display displayCtrl 80156) ctrlSetText str (((round (_realAzimuth * 6400 / 360)) + 6400) % 6400);
            } else {
                (_display displayCtrl 80156) ctrlSetText str ((round (_realAzimuth + 360)) % 360);
            };
        };

        //Update CurrentElevation Display and "charge" text
        if (_notGunnerView) then {
            (_display displayCtrl 80085) ctrlSetStructuredText parseText "";
            (_display displayCtrl 80175) ctrlSetText "";
        } else {
            (_display displayCtrl 80085) ctrlSetStructuredText parseText _chargeText;

            if (_useMils) then {
                (_display displayCtrl 80175) ctrlSetText str ((round (_realElevation * 6400 / 360)) % 6400);
            } else {
                (_display displayCtrl 80175) ctrlSetText str (((round (_realElevation * 100)) / 100) % 360);
            };
        };

        //Update ElevationNeeded Display:
        if (_notGunnerView || (!GVAR(allowComputerRangefinder))) then {
            (_display displayCtrl 80176) ctrlSetText "";
        } else {
            private _elevDeg = parseNumber ctrlText (_display displayCtrl 176);
            if (_elevDeg <= 0) then { //Bad data means "----" out of range
                (_display displayCtrl 80176) ctrlSetText (ctrlText (_display displayCtrl 176));
            } else {
                _elevDeg = _elevDeg + (_realElevation - (parseNumber ctrlText (_display displayCtrl 175)));
                if (_useMils) then {
                    (_display displayCtrl 80176) ctrlSetText str round ((round (_elevDeg * 6400 / 360)) % 6400);
                } else {
                    (_display displayCtrl 80176) ctrlSetText str (((round (_elevDeg * 100)) / 100) % 360);
                };
            };
        };
    };
}, 0, [_newVehicle, _fireModes]] call CBA_fnc_addPerFrameHandler;

/*
 * Author: Grey
 * Loads Magazine into static weapon
 *
 * Arguments:
 * 0: static <OBJECT>
 * 1: unit <OBJECT>
 * 2: magazine class to load; if not given the first compatible magazine is loaded <STRING> (default: "")
 *
 * Return Value:
 * None
 *
 * Example:
 * [_target,_player,"ACE_1Rnd_82mm_Mo_HE"] call ace_mk6mortar_fnc_loadMagazine
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit",["_magazineClassOptional","",[""]]];

//If function has been called with an optional classname hten add that magazine to the static weapon. Otherwise add the compatible magazine
if(_magazineClassOptional != "") then {
    _unit removeMagazine _magazineClassOptional;
    [QGVAR(addMagazine), [_static, _magazineClassOptional]] call CBA_fnc_globalEvent;
} else {
    //Get weapon & magazine information of static weapon
    private _weapon = (_static weaponsTurret [0]) select 0;
    private _currentMagazine = (magazinesAllTurrets _static) select 1;
    private _currentMagazineClass = _currentMagazine select 0;
    private _count = _currentMagazine select 2;

    //Check all of the players magazines to see if they are compatible with the static weapon. First magazine that is compatible is chosen
    //VKing: This section ought to be double checked.
    private _magazines = magazines _unit;
    private _magazineDetails = magazinesDetail _unit;
    private _listOfMagNames = getArray(configFile >> "cfgWeapons" >> _weapon >> "magazines");
    private _magazineClass = "";
    private _magazineClassDetails = "";
    private _roundsLeft = 0;
    {
        if (_x in _listOfMagNames) exitWith {
            _magazineClass = _x;
            _magazineClassDetails = _magazineDetails select _forEachIndex;
        };
    } forEach _magazines;
    //If the static weapon already has an empty magazine then remove it
    if (_count == 0) then {
        [QGVAR(removeMagazine), [_static, _currentMagazineClass]] call CBA_fnc_globalEvent;
    };
    //Find out the ammo count of the compatible magazine found
    if (_magazineClassDetails != "") then{
        private _parsed = _magazineClassDetails splitString "([]/: )";
        _parsed params ["_type", "", "", "_roundsLeftText", "_maxRoundsText"];
        _roundsLeft = parseNumber _roundsLeftText;
        _magType = _type;
    };

    _unit removeMagazine _magazineClass;
    [QGVAR(addMagazine), [_static, _magazineClass]] call CBA_fnc_globalEvent;
    [QGVAR(setAmmo), [_static, _magazineClass,_roundsLeft], _static] call CBA_fnc_targetEvent;
};

/*
 * Author: Grey
 * Loads Magazine into static weapon using a timer.
 *
 * Arguments:
 * 0: Static <OBJECT>
 * 1: Unit <OBJECT>
 * 2: Time to load <NUMBER>
 * 3: Magazine Class <STRING> (default: "")
 *
 * Return Value:
 * None
 *
 * Example:
 * [_target,_player,"ACE_1Rnd_82mm_Mo_HE"] call ace_mk6mortar_fnc_loadMagazineTimer
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit","_timeToLoad",["_magazineClassOptional","",[""]]];

// Move player into animation if player is standing
if ((_unit call CBA_fnc_getUnitAnim) select 0 == "stand") then {
    [_unit, "AmovPercMstpSrasWrflDnon_diary", 1] call EFUNC(common,doAnimation);
};

[_timeToLoad, [_static,_unit,_magazineClassOptional], {(_this select 0) call FUNC(loadMagazine)}, {}, localize LSTRING(loadingMortar)] call EFUNC(common,progressBar);

/*
 * Author: PabstMirror
 * Loads settings from the module.
 *
 * Arguments:
 * 0: The Module Logic Object <OBJECT>
 * 1: synced objects <ARRAY>
 * 2: Activated <BOOL>
 *
 * Return Value:
 * None
 *
 * Example:
 * [fromModule] call ace_mk6mortar_fnc_moduleInit
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_logic", "_syncedUnits", "_activated"];

if (!_activated) exitWith {WARNING("Module - placed but not active");};

[_logic, QGVAR(airResistanceEnabled), "airResistanceEnabled"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(allowComputerRangefinder), "allowComputerRangefinder"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(allowCompass), "allowCompass"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(useAmmoHandling), "useAmmoHandling"] call EFUNC(common,readSettingFromModule);

/*
 * Author: VKing
 * Initializes mortar for use with ammunition handling magazines.
 *
 * Arguments:
 * 0: Mortar <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [mortar1] call ace_mk6mortar_fnc_mortarInit
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_mortar"];

if (_mortar getVariable [QGVAR(initialized),false] || _mortar getVariable [QGVAR(exclude),false]) exitWith {TRACE_1("Exit",_mortar)};
if (!(_mortar turretLocal [0])) exitWith {TRACE_1("Exit - turret not local",_mortar)};

// Remove all magazines from turret
if (count magazines _mortar > 0) then {
    {
        _mortar removeMagazineTurret [_x,[0]];
    } forEach magazines _mortar;
};

// Replace current turret weapon with ammo handling weapon
private _currentWeapon = _mortar weaponsTurret [0] select 0;
private _newWeapon = "";

if (_currentWeapon == "mortar_82mm") then {
    _newWeapon = "ace_mortar_82mm";
} else {
    _newWeapon = getText (configFile >> "CfgWeapons" >> _currentWeapon >> QGVAR(replaceWith));
};

if (_newWeapon != "") then {
    _mortar removeWeaponTurret [_currentWeapon,[0]];
    _mortar addWeaponTurret [_newWeapon,[0]];
};

_mortar setVariable [QGVAR(initialized),true,true];
TRACE_1("Init complete",_mortar);

/*
 * Author: PabstMirror
 * Opens the rangetable and fills the charge listbox.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_mk6mortar_fnc_rangeTableOpen
 *
 * Public: No
 */
#include "script_component.hpp"

#define LIST_CHARGE ((uiNamespace getVariable "ACE_82mm_RangeTable_Dialog") displayCtrl 1501)

private _weaponName = "mortar_82mm"; //todo: work on other weapons

createDialog "ACE_82mm_RangeTable_Dialog";
if (isNull (uiNamespace getVariable ["ACE_82mm_RangeTable_Dialog", displayNull])) exitWith {ERROR("Dialog failed to open");};

//Get Magazine Types
private _magazines = getArray (configFile >> "CfgWeapons" >> _weaponName >> "magazines");

//For now just get settings from first mag, all rounds have same flight characteristics:
if ((count _magazines) < 1) exitWith {ERROR("No Magazines for weapon");};
private _initSpeed = getNumber (configFile >> "CfgMagazines" >> (_magazines select 0) >> "initSpeed");

//Get Charge Modes
private _fireModes = getArray (configFile >> "CfgWeapons" >> _weaponName >> "modes");

private _muzzleVelocities = [];
{
    private _showToPlayer = getNumber (configFile >> "CfgWeapons" >> _weaponName >> _x >> "showToPlayer");
    if (_showToPlayer == 1) then {
        private _artilleryCharge = getNumber (configFile >> "CfgWeapons" >> _weaponName >> _x >> "artilleryCharge");
        LIST_CHARGE lbAdd format ["%1: %2", (localize LSTRING(rangetable_charge)), (count _muzzleVelocities)];
        LIST_CHARGE lbSetData [(count _muzzleVelocities), str (_artilleryCharge * _initSpeed)];
        _muzzleVelocities pushBack _artilleryCharge;
    };
} forEach _fireModes;

LIST_CHARGE lbSetCurSel 0;

/*
 * Author: PabstMirror
 * Called when listbox selection changes.  Updates the rangetable with new values.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_mk6mortar_fnc_rangeTablePageChange
 *
 * Public: No
 */
#include "script_component.hpp"

#define RANGE_TABLE       ((uiNamespace getVariable "ACE_82mm_RangeTable_Dialog") displayCtrl 20001)
#define LIST_CHARGE       ((uiNamespace getVariable "ACE_82mm_RangeTable_Dialog") displayCtrl 1501)

private _listBoxData = LIST_CHARGE lbData (lbCurSel LIST_CHARGE);
if (isNil "_listBoxData" || {_listBoxData == ""}) exitWith {ERROR("lbCurSel out of bounds or no data");};
private _muzzleVelocity = parseNumber _listBoxData;

private _airFriction = if (GVAR(airResistanceEnabled)) then {MK6_82mm_AIR_FRICTION} else {0};

private _precalcArray = [_muzzleVelocity, _airFriction] call FUNC(rangeTablePreCalculatedValues);

lnbClear RANGE_TABLE;
{
    RANGE_TABLE lnbAddRow _x;
} forEach _precalcArray;

//put dummy line at end because scrolling is fucked and can't see last line
RANGE_TABLE lnbAddRow ["", "", "", "", "", "", "", "", "", "", ""];

/*
 * Author: PabstMirror
 * Toggles the mortart to show mils or degrees
 *
 * Arguments:
 * 0: Vehicle <OBJECT>
 * 1: Player <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [mortar,bob] call ace_mk6mortar_fnc_toggleMils;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_mortarVeh", "_unit"];

private _currentSetting = _mortarVeh getVariable [QGVAR(useMils), true];
_mortarVeh setVariable [QGVAR(useMils), (!_currentSetting)];

/*
 * Author: PabstMirror
 * Called when the mk6's in game UI is loaded.  Hides rangefinder data if it is disabled.
 *
 * Arguments:
 * 0: Display <DISPLAY>
 * 1: Type <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * [rsc,"Mk6Mortar"] call ace_mk6mortar_fnc_turretDisplayLoaded
 *
 * Public: No
 */
#include "script_component.hpp"

disableSerialization;

params ["_display", "_rscType"];
TRACE_2("params",_display,_rscType);

if (_rscType != "Mk6Mortar") exitWith {};
if (isNull _display) exitWith {};

#define CTRL_CA_OPTICSPITCH (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_OPTICSPITCH")
#define CTRL_CA_OPTICSZOOM (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_OPTICSZOOM")
#define CTRL_CA_SOLUTION_TEXT (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_SOLUTION_TEXT")
#define CTRL_CA_DISTANCE_TEXT (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_DISTANCE_TEXT")
#define CTRL_CA_DISTANCE (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_DISTANCE")
#define CTRL_CA_TIME_TEXT (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_TIME_TEXT")
#define CTRL_CA_TIME (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_TIME")
#define CTRL_CA_ELEV_NEED_TEXT (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_ELEV_NEED_TEXT")
#define CTRL_CA_RANGEELEMENTS_GROUP (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_RangeElements_group")

#define CTRL_CA_HEADING (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_Heading")
#define CTRL_CA_ELEV (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_ELEV")
#define CTRL_CA_ELEV_NEED (configFile >> "RscInGameUI" >> "ACE_Mk6_RscWeaponRangeArtillery" >> "CA_IGUI_elements_group" >> "controls" >> "CA_ELEV_NEED")

private _fnc_hideControl = {
    params ["_path", "_hideCtrl"];
    private _idc = getNumber (_path >> "IDC");
    private _pos = [];
    if (_hideCtrl) then {
        _pos = [-9,-9,0,0];
    } else {
        {
            if (isNumber (_path >> _x)) then {
                _pos pushBack (getNumber (_path >> _x));
            } else {
                _pos pushBack (call compile (getText (_path >> _x)));
            };
        } forEach ["x", "y", "w", "h"];
    };
    (_display displayCtrl _idc) ctrlSetPosition _pos;
    (_display displayCtrl _idc) ctrlCommit 0;
};

{
    [_x, (!GVAR(allowComputerRangefinder))] call _fnc_hideControl;
} forEach [CTRL_CA_OPTICSPITCH, CTRL_CA_OPTICSZOOM, CTRL_CA_SOLUTION_TEXT, CTRL_CA_DISTANCE_TEXT, CTRL_CA_DISTANCE, CTRL_CA_TIME_TEXT, CTRL_CA_TIME, CTRL_CA_ELEV_NEED_TEXT, CTRL_CA_RANGEELEMENTS_GROUP];

//Always hide these, handled by the mil convertions PFEH in handlePlayerVehChanged
[CTRL_CA_HEADING, true] call _fnc_hideControl;
[CTRL_CA_ELEV, true] call _fnc_hideControl;
[CTRL_CA_ELEV_NEED, true] call _fnc_hideControl;

/*
 * Author: Grey
 *
 * Unload current magazine from static weapon
 *
 * Arguments:
 * 0: static <OBJECT>
 * 1: unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [_target, _player] call ace_mk6mortar_fnc_unloadMagazine
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit"];

//Get weapon & magazine information about static weapon
private _currentMagazine = (magazinesAllTurrets _static) select 1;
private _currentMagazineClass = _currentMagazine select 0;
private _ammoCount = _currentMagazine select 2;

// Try to add the round to player inventory, otherwise place it on the ground near the player
if (_ammoCount > 0) then {
    if (_unit canAdd _currentMagazineClass) then {
        _unit addMagazineGlobal _currentMagazineClass;
    } else {
        _pos = _unit modelToWorldVisual [0.5,0.5,0]; // Front right of player
        _unit = createVehicle ["WeaponHolder_Single_F",_pos,[],0,"NONE"];
        _unit addMagazineAmmoCargo [_currentMagazineClass, 1, _ammoCount];
        _unit setPosATL _pos;
    };
    [QGVAR(removeMagazine), [_static, _currentMagazineClass]] call CBA_fnc_globalEvent;
};

/*
 * Author: Grey
 *
 * Unload current magazine from static weapon using a timer
 *
 * Arguments:
 * 0: static <OBJECT>
 * 1: unit <OBJECT>
 * 2: time to unload <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [_target, _player, 5] call ace_mk6mortar_fnc_unloadMagazineTimer
 *
 * Public: Yes
 */
#include "script_component.hpp"

params ["_static","_unit","_timeToUnload"];

//Move player into animation if player is standing
if ((_unit call CBA_fnc_getUnitAnim) select 0 == "stand") then {
    [_unit, "AmovPercMstpSrasWrflDnon_diary", 1] call EFUNC(common,doAnimation);
};

[_timeToUnload, [_static,_unit], {(_this select 0) call FUNC(unloadMagazine)}, {}, localize LSTRING(unloadingMortar)] call EFUNC(common,progressBar);

