/*
 * Author: aeroson
 * Shows the actual text and sets text the crew info.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * call ace_nametags_fnc_doShow
 *
 * Public: No
 */
#include "script_component.hpp"
#include "common.hpp"

private _player = ACE_player;
private _vehicle = vehicle _player;
private _type = typeOf _vehicle;
private _config = configFile >> "CfgVehicles" >> _type;
private _text = format["<t size='1.4'><img image='%1'></t> <t size='1.7' shadow='true'>%2</t><br/>", getText(_config>>"picture"), getText (_config >> "DisplayName")];

private _data = [_type] call FUNC(getVehicleData);

private _isAir = _data select 0;
private _data = _data select 1;

private _turretUnits = _data apply {_vehicle turretUnit (_x select 0)};
private _turretRoles = _data apply {_x select 1};

private _roleType = CARGO;
private _toShow = [];
{
    switch (_x) do {
        case commander _vehicle: {
            _roleType = COMMANDER;
        };
        case gunner _vehicle: {
            _roleType = GUNNER;
        };
        case driver _vehicle: {
            _roleType = if(_isAir) then { PILOT } else { DRIVER };
        };
        default {
            _index = _turretUnits find _x;
            if(_index !=-1 ) then {
                _roleType = _turretRoles select _index;
            } else {
                _roleType = CARGO;
            };
        };
    };
    _toShow pushBack [_x, _roleType];
} forEach crew _vehicle;

_toShow = [
    _toShow,
    [],
    {
        _x select 1
    },
    "ASCEND",
    {
        _unit = _x select 0;
        alive _unit
    }
] call BIS_fnc_sortBy;

private _roleImages = ROLE_IMAGES;
{
    private _unit = _x select 0;
    private _roleType = _x select 1;
    _text = _text + format["<t size='1.5' shadow='true'>%1</t> <t size='1.3'><img image='%2'></t><br/>", [_unit] call EFUNC(common,getName), _roleImages select _roleType];
} forEach _toShow;

("ACE_CrewInfo_CrewInfo" call BIS_fnc_rscLayer) cutRsc ["ACE_CrewInfo_dialog", "PLAIN", 1, false];

terminate (missionNamespace getVariable [QGVAR(hideCrewInfoHandle), scriptNull]);
GVAR(hideCrewInfoHandle) = 0 spawn {
    sleep 2;
    ("ACE_CrewInfo_CrewInfo" call BIS_fnc_rscLayer) cutFadeOut 2;
};

[_text] call FUNC(setText);

/*
 * Author: commy2, esteldunedain
 * Draw the nametag and rank icon.
 *
 * Arguments:
 * 0: Unit (Player) <OBJECT>
 * 1: Target <OBJECT>
 * 2: Alpha <NUMBER>
 * 4: Height offset <NUMBER>
 * 5: Draw name <BOOL>
 * 5: Draw rank <BOOL>
 * 6: Draw soundwave <BOOL>
 *
 * Return Value:
 * None
 *
 * Example:
 * [ACE_player, bob, 0.5, height, true, true, true] call ace_nametags_fnc_drawNameTagIcon
 *
 * Public: No
 */

#include "script_component.hpp"

TRACE_1("drawName:", _this);

params ["", "_target", "", "_heightOffset"];

_fnc_parameters = {
    params ["_player", "_target", "_alpha", "_heightOffset", "_drawName", "_drawRank", "_drawSoundwave"];

    //Set Icon:
    private _icon = "";
    private _size = 0;
    if (_drawSoundwave) then {
        _icon = format [QPATHTOF(UI\soundwave%1.paa), floor random 10];
        _size = 1;
    } else {
        if (_drawRank && {rank _target != ""}) then {
            _icon = GVAR(factionRanks) getVariable (_target getVariable [QGVAR(faction), faction _target]);
            if (!isNil "_icon") then {
                _icon = _icon param [ALL_RANKS find rank _target, ""];
            } else {
                _icon = format ["\A3\Ui_f\data\GUI\Cfg\Ranks\%1_gs.paa", rank _target];
            };
            _size = 1;
        };
    };

    //Set Text:
    private _name = if (_drawName) then {
        [_target, true, true] call EFUNC(common,getName)
    } else {
        ""
    };

    //Set Color:
    private _color = [1, 1, 1, _alpha];
    if ((group _target) != (group _player)) then {
        _color = +GVAR(defaultNametagColor); //Make a copy, then multiply both alpha values (allows client to decrease alpha in settings)
        _color set [3, (_color select 3) * _alpha];
    } else {
        _color = [[1, 1, 1, _alpha], [1, 0, 0, _alpha], [0, 1, 0, _alpha], [0, 0, 1, _alpha], [1, 1, 0, _alpha]] select ((["MAIN", "RED", "GREEN", "BLUE", "YELLOW"] find ([assignedTeam _target] param [0, "MAIN"])) max 0);
    };

    private _scale = [0.333, 0.5, 0.666, 0.83333, 1] select GVAR(tagSize);

    [
        _icon,
        _color,
        [],
        (_size * _scale),
        (_size * _scale),
        0,
        _name,
        2,
        (0.05 * _scale),
        "RobotoCondensed"
    ]
};

private _parameters = [_this, _fnc_parameters, _target, QGVAR(drawParameters), 0.1] call EFUNC(common,cachedCall);
_parameters set [2, _target modelToWorldVisual ((_target selectionPosition "pilot") vectorAdd [0,0,(_heightOffset + .3)])];


drawIcon3D _parameters;

/*
 * Author: <N/A>
 * Get's flags used for onDraw3D that can be cached
 *
 * Arguments:
 * None
 *
 * Return Value:
 * [_drawName,_drawRank,_enabledTagsNearby,_enabledTagsCursor,_maxDistance] <ARRAY>
 *
 * Example:
 * call ace_nametags_fnc_getCachedFlags
 *
 * Public: No
 */
#include "script_component.hpp"

// Determine flags from current settings
private _drawName = true;
private _enabledTagsNearby = false;
private _enabledTagsCursor = false;

switch (GVAR(showPlayerNames)) do {
    case 0: {
        // Player names Disabled [Note: this should be unreachable as the drawEH will be removed]
        _drawName = false;
        _enabledTagsNearby = (GVAR(showSoundWaves) == 2);
    };
    case 1: {
        // Player names Enabled
        _enabledTagsNearby = true;
    };
    case 2: {
        // Player names Only cursor
        _enabledTagsNearby = (GVAR(showSoundWaves) == 2);
        _enabledTagsCursor = true;
    };
    case 3: {
        // Player names Only Keypress
        _enabledTagsNearby = GVAR(showSoundWaves) == 2; // non-cached: || _onKeyPressAlphaMax) > 0
    };
    case 4: {
        // Player names Only Cursor and Keypress
        _enabledTagsNearby = (GVAR(showSoundWaves) == 2);
        // non-cached: _enabledTagsCursor = _onKeyPressAlphaMax > 0;
    };
    case 5: {
        // Fade on border
        _enabledTagsNearby = true;
    };
};

private _ambientBrightness = ((([] call EFUNC(common,ambientBrightness)) + ([0, 0.4] select ((currentVisionMode ace_player) != 0))) min 1) max 0;
private _maxDistance = _ambientBrightness * GVAR(PlayerNamesViewDistance);

[_drawName, GVAR(showPlayerRanks),_enabledTagsNearby,_enabledTagsCursor,_maxDistance]

/*
 * Author: aeroson
 * Gathers and caches data needed by ace_nametags_fnc_doShow.
 * What really does make difference for the engine is simulation of CfgAmmo.
 * Priority of roles  is: driver/pilot, gunner, copilot, commander, FFV, cargo.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * Data <ARRAY>
 * 0: Vehicle inherits from Air <BOOL>
 * 1: Categorized vehicle's turrets <ARRAY>
 *
 * Example:
 * call ace_nametags_fnc_updateSettings
 *
 * Public: No
 */
#include "script_component.hpp"
#include "common.hpp"

params ["_type"];

private _varName = format ["ACE_CrewInfo_Cache_%1", _type];
private _data = + (uiNamespace getVariable _varName);

if (!isNil "_data") exitWith {_data};

_data = [];
private _isAir = _type isKindOf "Air";

private _fnc_addTurretUnit = {
    params ["_config", "_path"];
    private _role = CARGO;

    private _simulationEmpty = 0;
    private _simulationLaserDesignate = 0;
    private _simulationOther = 0;
    {
        {
            private _magazine = configFile >> "CfgMagazines" >> _x;
            private _ammo = configFile >> "CfgAmmo" >> getText (_magazine >> "ammo");
            private _simulation = getText (_ammo >> "simulation");

            if(_simulation=="") then {
                _simulationEmpty = _simulationEmpty + 1;
            } else {
                if(_simulation=="laserDesignate") then {
                    _simulationLaserDesignate = _simulationLaserDesignate + 1;
                } else {
                    _simulationOther = _simulationOther + 1;
                };
            };

        } forEach getArray (configFile >> "CfgWeapons" >> _x >> "magazines");
    } forEach getArray (_config >> "weapons");

    if(_simulationOther>0) then {
        _role = GUNNER;
    };
    if (_role == CARGO && {getNumber (_config >> "isCopilot") == 1}) then {
        _role = COPILOT;
    };
    if (_role == CARGO && {_simulationLaserDesignate>0 || getNumber (_config >> "primaryObserver") == 1}) then {
        _role = COMMANDER;
    };
    if (_role == CARGO && {getNumber (_config >> "isPersonTurret") == 1}) then {
        _role = FFV;
    };

    _data pushBack [_path, _role];  
};


private _fnc_addTurret = {
    params ["_config", "_path"];

    _config = _config >> "Turrets";
    private _count = count _config;

    private _offset = 0;

    for "_index" from 0 to (_count - 1) do {
        private _turretPath = _path + [_index - _offset];
        private _turretConfig = _config select _index;
        if (isClass _turretConfig) then {           
            [_turretConfig, _turretPath] call _fnc_addTurretUnit;
            [_turretConfig, _turretPath] call _fnc_addTurret;
        } else {
            _offset = _offset + 1;
        };
    };
};


_config = configFile >> "CfgVehicles" >> _type;
[_config, []] call _fnc_addTurret;

_data = [_isAir, _data];
uiNamespace setVariable [_varName, _data];

_data

/*
 * Author: Glowbal, PabstMirror
 * Starts up a PFEH to monitor the when players are talking.
 * Compatiblity with TFR/ACRE and Arma's VON
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_nametags_fnc_initIsSpeaking
 *
 * Public: No
 */
#include "script_component.hpp"

if (isServer) then {
    //If someone disconnects while speaking, reset their variable
    addMissionEventHandler ["HandleDisconnect", {
        params ["_disconnectedPlayer"];
        if (_disconnectedPlayer getVariable [QGVAR(isSpeakingInGame), false]) then {
            _disconnectedPlayer setVariable [QGVAR(isSpeakingInGame), false, true];
        };
    }];
};

if (!hasInterface) exitWith {};

["unit", {
    //When player changes, make sure to reset old unit's variable
    params ["", "_oldUnit"];
    if ((!isNull _oldUnit) && {_oldUnit getVariable [QGVAR(isSpeakingInGame), false]}) then {
        _oldUnit setVariable [QGVAR(isSpeakingInGame), false, true];
    };
}] call CBA_fnc_addPlayerEventHandler;

if (isClass (configFile >> "CfgPatches" >> "acre_api")) then {
    INFO("ACRE Detected.");
    DFUNC(isSpeaking) = {
        params ["_unit"];
        ([_unit] call acre_api_fnc_isSpeaking) && {!(_unit getVariable ["ACE_isUnconscious", false])}
    };
} else {
    if (isClass (configFile >> "CfgPatches" >> "task_force_radio")) then {
        INFO("TFR Detected.");
        DFUNC(isSpeaking) =     {
            params ["_unit"];
            (_unit getVariable ["tf_isSpeaking", false]) && {!(_unit getVariable ["ACE_isUnconscious", false])}
        };
    } else {
        //No Radio Mod - Start a PFEH to watch the internal VON icon
        //Note: class RscDisplayVoiceChat {idd = 55} - only present when talking

        [{
            private _oldSetting = ACE_player getVariable [QGVAR(isSpeakingInGame), false];
            private _newSetting = (!(isNull findDisplay 55));
            if (!(_oldSetting isEqualTo _newSetting)) then {
                ACE_player setVariable [QGVAR(isSpeakingInGame), _newSetting, true];
            };
        } , 0.1, []] call CBA_fnc_addPerFrameHandler;

        DFUNC(isSpeaking) = {
            params ["_unit"];
            (_unit getVariable [QGVAR(isSpeakingInGame), false]) && {!(_unit getVariable ["ACE_isUnconscious", false])}
        };
    };
};

/*
 * Author: esteldunedain
 * Initializes the name tags module.
 *
 * Arguments:
 * Whatever the module provides.
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_nametags_fnc_moduleNameTags
 *
 * Public: No
 */

#include "script_component.hpp"

params ["_logic", "", "_activated"];

if !(_activated) exitWith {};

GVAR(Module) = true;

[_logic, QGVAR(playerNamesViewDistance),  "playerNamesViewDistance"     ] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(showCursorTagForVehicles), "showCursorTagForVehicles"    ] call EFUNC(common,readSettingFromModule);

// Do Not Force - read module setting only non-default is set due to using SCALAR
if ((_logic getVariable "showPlayerNames") != -1) then {
    [_logic, QGVAR(showPlayerNames),      "showPlayerNames"             ] call EFUNC(common,readSettingFromModule);
};
if ((_logic getVariable "showNamesForAI") != -1) then {
    [_logic, QGVAR(showNamesForAI),      "showNamesForAI"             ] call EFUNC(common,readSettingFromModule);
};
if ((_logic getVariable "showVehicleCrewInfo") != -1) then {
    [_logic, QGVAR(showVehicleCrewInfo),  "showVehicleCrewInfo"         ] call EFUNC(common,readSettingFromModule);
};

INFO("Nametags Module Initialized.");

/*
 * Author: <N/A>
 * Draws names and icons.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * call ace_nametags_fnc_onDraw3d
 *
 * Public: No
 */
#include "script_component.hpp"

BEGIN_COUNTER(GVAR(onDraw3d));

// Don't show nametags in spectator or if RscDisplayMPInterrupt is open
if ((isNull ACE_player) || {!alive ACE_player} || {!isNull (findDisplay 49)}) exitWith {};

private _flags = [[], DFUNC(getCachedFlags), ACE_player, QGVAR(flagsCache), 2] call EFUNC(common,cachedCall);

_flags params ["_drawName", "_drawRank", "_enabledTagsNearby", "_enabledTagsCursor", "_maxDistance"];

private _onKeyPressAlphaMax = 1;
if (GVAR(showPlayerNames) == 3) then {
    _onKeyPressAlphaMax = 2 + (GVAR(showNamesTime) - CBA_missionTime);
    _enabledTagsNearby = _enabledTagsNearby || {_onKeyPressAlphaMax > 0}
};
if (GVAR(showPlayerNames) == 4) then {
    _onKeyPressAlphaMax = 2 + (GVAR(showNamesTime) - CBA_missionTime);
    _enabledTagsCursor = _onKeyPressAlphaMax > 0;
};

private _camPosAGL = positionCameraToWorld [0, 0, 0];
if !((_camPosAGL select 0) isEqualType 0) exitWith {}; // handle RHS / bugged vehicle slots

private _camPosASL = AGLtoASL _camPosAGL;

// Show nametag for the unit behind the cursor or its commander
if (_enabledTagsCursor) then {
    private _target = cursorTarget;
    if !(_target isKindOf "CAManBase") then {
        // When cursorTarget is on a vehicle show the nametag for the commander.
        if !(_target in allUnitsUAV) then {
            _target = effectiveCommander _target;
        } else {
            _target = objNull;
        };
    };
    if (isNull _target) exitWith {};

    if (_target != ACE_player &&
        {(side group _target) == (side group ACE_player)} &&
        {GVAR(showNamesForAI) || {[_target] call EFUNC(common,isPlayer)}} &&
        {lineIntersectsSurfaces [_camPosASL, eyePos _target, ACE_player, _target] isEqualTo []} &&
        {!isObjectHidden _target}) then {

        private _distance = ACE_player distance _target;

        private _drawSoundwave = (GVAR(showSoundWaves) > 0) && {[_target] call FUNC(isSpeaking)};
        // Alpha:
        // - base value determined by GVAR(playerNamesMaxAlpha)
        // - decreases when _distance > _maxDistance
        // - increases when the unit is speaking
        // - it's clamped by the value of _onKeyPressAlphaMax
        private _alpha = (((1 + ([0, 0.2] select _drawSoundwave) - 0.2 * (_distance - _maxDistance)) min 1) * GVAR(playerNamesMaxAlpha)) min _onKeyPressAlphaMax;

        if (_alpha > 0) then {
            [ACE_player, _target, _alpha, _distance * 0.026, _drawName, _drawRank, _drawSoundwave] call FUNC(drawNameTagIcon);
        };
    };
};

// Show nametags for nearby units
if (_enabledTagsNearby) then {
    // Find valid targets and cache them
    private _targets = [[], {
        private _nearMen = _camPosAGL nearObjects ["CAManBase", _maxDistance + 7];
        _nearMen = _nearMen select {
            _x != ACE_player &&
            {(side group _x) == (side group ACE_player)} &&
            {GVAR(showNamesForAI) || {[_x] call EFUNC(common,isPlayer)}} &&
            {lineIntersectsSurfaces [_camPosASL, eyePos _x, ACE_player, _x] isEqualTo []} &&
            {!isObjectHidden _x}
        };
        private _crewMen = [];
        if (vehicle ACE_player != ACE_player) then {
            _crewMen = (crew vehicle ACE_player) select {
                _x != ACE_player &&
                {(side group _x) == (side group ACE_player)} &&
                {GVAR(showNamesForAI) || {[_x] call EFUNC(common,isPlayer)}} &&
                {lineIntersectsSurfaces [_camPosASL, eyePos _x, ACE_player, _x, true, 1, "GEOM", "NONE"] isEqualTo []} &&
                {!isObjectHidden _x}
            };
        };
        (_nearMen + _crewMen)
    }, missionNamespace, QGVAR(nearMen), 0.5] call EFUNC(common,cachedCall);

    {
        private _target = _x;

        if !(isNull _target) then {
            private _drawSoundwave = (GVAR(showSoundWaves) > 0) && {[_target] call FUNC(isSpeaking)};
            if (_enabledTagsCursor && {!_drawSoundwave}) exitWith {}; // (Cursor Only && showSoundWaves==2) - quick exit

            private _relPos = (visiblePositionASL _target) vectorDiff _camPosASL;
            private _distance = vectorMagnitude _relPos;

            // Fade on border
            private _centerOffsetFactor = 1;
            if (GVAR(showPlayerNames) == 5) then {
                private _screenPos = worldToScreen (_target modelToWorld (_target selectionPosition "head"));
                if !(_screenPos isEqualTo []) then {
                    // Distance from center / half of screen width
                    _centerOffsetFactor = 1 - ((_screenPos distance2D [0.5, 0.5]) / (safezoneW / 3));
                } else {
                    _centerOffsetFactor = 0;
                };
            };

            private _alphaMax = _onKeyPressAlphaMax;
            if ((GVAR(showSoundWaves) == 2) && _drawSoundwave) then {
                _drawName = _drawSoundwave;
                _drawRank = false;
                _alphaMax = 1;
            };
            // Alpha:
            // - base value determined by GVAR(playerNamesMaxAlpha)
            // - decreases when _distance > _maxDistance
            // - increases when the unit is speaking
            // - it's clamped by the value of _onKeyPressAlphaMax unless soundwaves are forced on and the unit is talking
            private _alpha = (((1 + ([0, 0.2] select _drawSoundwave) - 0.2 * (_distance - _maxDistance)) min 1) * GVAR(playerNamesMaxAlpha) * _centerOffsetFactor) min _alphaMax;

            if (_alpha > 0) then {
                [ACE_player, _target, _alpha, _distance * 0.026, _drawName, _drawRank, _drawSoundwave] call FUNC(drawNameTagIcon);
            };
        };
        nil
    } count _targets;
};

END_COUNTER(GVAR(onDraw3d));

/*
 * Author: aeroson
 * Sets the text on the dialog.
 *
 * Arguments:
 * 0: Text <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * call ace_nametags_fnc_setText
 *
 * Public: No
 */
#include "script_component.hpp"

#define TextIDC 11123

params ["_text"];

disableSerialization;

private _ctrl = (uiNamespace getVariable QGVAR(dialog)) displayCtrl TextIDC;
_ctrl ctrlSetStructuredText parseText _text;
_ctrl ctrlCommit 0;

/*
 * Author: Jonpas
 * Dynamically adds and removes Draw3D based on settings on run-time.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * call ace_nametags_fnc_updateSettings
 *
 * Public: No
 */
#include "script_component.hpp"

if (isNil QGVAR(drawHandler) && {GVAR(showPlayerNames) != 0}) then {
    GVAR(drawHandler) = addMissionEventHandler ["Draw3D", {_this call FUNC(onDraw3d);}];
} else {
    if (!isNil QGVAR(drawHandler) && {GVAR(showPlayerNames) == 0}) then {
        removeMissionEventHandler ["Draw3D", GVAR(drawHandler)];
        GVAR(drawHandler) = nil;
    };
};

