/*
 * Author: GitHawk
 * Rearm an entire vehicle.
 *
 * Arguments:
 * 0: Rearm information <ARRAY>
 *   0: Ammo Truck <OBJECT>
 *   1: Vehicle <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [[ammo_truck, tank]] call ace_rearm_fnc_rearmEntireVehicleSuccess
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_args"];
_args params ["_truck", "_vehicle"];
TRACE_2("rearmEntireVehicleSuccess",_truck,_vehicle);

if (isServer) then {
    private _turrets = [_vehicle] call FUNC(getAllRearmTurrets);
    {
        private _turretOwnerID = _vehicle turretOwner _x;
        if (_turretOwnerID == 0) then {
            [QGVAR(rearmEntireVehicleSuccessLocalEH), [_truck, _vehicle, _x], [_vehicle]] call CBA_fnc_targetEvent;
        } else {
            [QGVAR(rearmEntireVehicleSuccessLocalEH), [_truck, _vehicle, _x], _turretOwnerID] call CBA_fnc_ownerEvent;
        };
        false
    } count _turrets;
} else {
    [QGVAR(rearmEntireVehicleSuccessEH), _this] call CBA_fnc_serverEvent;
};

/*
 * Author: Tuupertunut
 * Rearm an entire turret locally.
 *
 * Arguments:
 * 0: Ammo Truck <OBJECT>
 * 1: Vehicle <OBJECT>
 * 2: TurretPath <ARRAY>
 *
 * Return Value:
 * None
 *
 * Example:
 * [ammo_truck, tank, [0]] call ace_rearm_fnc_rearmEntireVehicleSuccessLocal
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_truck", "_vehicle", "_turretPath"];
TRACE_3("rearmEntireVehicleSuccessLocal",_truck,_vehicle,_turretPath);

// Fetching all rearmable magazines in this turret
private _magazines = ([_vehicle] call FUNC(getNeedRearmMagazines)) select {(_x select 1) isEqualTo _turretPath};
{
    _x params ["_magazineClass", "_magTurretPath", "_isPylonMag", "_pylonIndex", "_maxMagazines", "_currentMagazines", "_maxRoundsPerMag", "_currentRounds"];

    // Array of planned ammo counts in every magazine after the rearm is complete
    private _plannedRounds = +_currentRounds;

    // Trying to fill all existing magazines.
    {
        if (_x < _maxRoundsPerMag) then {
            if ((GVAR(supply) == 0) || {[_truck, _magazineClass, (_maxRoundsPerMag - _x)] call FUNC(removeMagazineFromSupply)}) then {
                _plannedRounds set [_forEachIndex, _maxRoundsPerMag];
            };
        };
    } forEach _currentRounds;

    // Trying to add new full magazines, if there is space left.
    if (_currentMagazines < _maxMagazines) then {
        for "_idx" from 1 to (_maxMagazines - _currentMagazines) do {
            if ((GVAR(supply) == 0) || {[_truck, _magazineClass, _maxRoundsPerMag] call FUNC(removeMagazineFromSupply)}) then {
                _plannedRounds pushBack _maxRoundsPerMag;
            };
        };
    };

    TRACE_2("rearming",_x,_plannedRounds);

    // Updating new ammo counts to vehicle.
    if (_isPylonMag) then {
        _vehicle setAmmoOnPylon [_pylonIndex, (_plannedRounds select 0)];
    } else {
        [_vehicle, _magTurretPath, _magazineClass, _plannedRounds] call FUNC(setTurretMagazineAmmo);
    };
} forEach _magazines;

/*
 * Author: GitHawk
 * Rearms a vehicle, after progress bar finishes, pass args to machine where turret is local.
 *
 * Arguments:
 * 0: Vehicle <OBJECT>
 * 1: Unit <OBJECT>
 * 2: Turret Path <ARRAY>
 * 3: Number of magazines <NUMBER>
 * 4: Magazine Classname <STRING>
 * 5: Number of rounds <NUMBER>
 * 6: Pylon Index <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [vehicle, player, [-1], 2, "5000Rnd_762x51_Belt", 500, -1] call ace_rearm_fnc_rearmSuccess
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_vehicle", "_unit", "_turretPath", "_numMagazines", "_magazineClass", "_numRounds", "_pylon"];
TRACE_7("rearmSuccess",_vehicle,_unit,_turretPath,_numMagazines,_magazineClass,_numRounds,_pylon);

if (local _unit) then {
    [_unit, true, true] call FUNC(dropAmmo);
};

if (!alive _vehicle) exitWith {WARNING("vehicle dead/null");};

if (isServer) then {
    private _turretOwnerID = _vehicle turretOwner _turretPath;
    if (_turretOwnerID == 0) then {
        [QGVAR(rearmSuccessLocalEH), _this, [_vehicle]] call CBA_fnc_targetEvent;
    } else {
        [QGVAR(rearmSuccessLocalEH), _this, _turretOwnerID] call CBA_fnc_ownerEvent;
    };
} else {
    [QGVAR(rearmSuccessEH), _this] call CBA_fnc_serverEvent;
};

/*
 * Author: GitHawk
 * Rearms a vehicle on the turret owner.
 *
 * Arguments:
 * 0: Vehicle <OBJECT>
 * 1: Unit <OBJECT>
 * 2: Turret Path <ARRAY>
 * 3: Number of magazines <NUMBER>
 * 4: Magazine Classname <STRING>
 * 5: Number of rounds <NUMBER>
 * 6: Pylon Index <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [vehicle, player, [-1], 2, "5000Rnd_762x51_Belt", 500, ""] call ace_rearm_fnc_rearmSuccessLocal
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_vehicle", "_unit", "_turretPath", "_numMagazines", "_magazineClass", "_numRounds", "_pylon"];
TRACE_7("rearmSuccessLocal",_vehicle,_unit,_turretPath,_numMagazines,_magazineClass,_numRounds,_pylon);

private _rounds = getNumber (configFile >> "CfgMagazines" >> _magazineClass >> "count");

if (_pylon > 0) exitWith {
    if (_turretPath isEqualTo [-1]) then {_turretPath = [];}; // Convert back to pylon turret format
    private _currentCount = _vehicle ammoOnPylon _pylon;
    private _newCount = ((_currentCount max 0) + _numRounds) min _rounds;
    TRACE_2("",_pylon,_magazineClass,_newCount);
    _vehicle setPylonLoadOut [_pylon, _magazineClass, false, _turretPath];
    _vehicle setAmmoOnPylon [_pylon, _newCount];
};

private _currentRounds = 0;
private _maxMagazines = [_vehicle, _turretPath, _magazineClass] call FUNC(getMaxMagazines);

if (_maxMagazines == 1) then {
    private _currentMagazines = { _x == _magazineClass } count (_vehicle magazinesTurret _turretPath);
    if (_currentMagazines == 0 && {!(_turretPath isEqualTo [-1])}) then {
        // Driver gun will always retain it's magazines
        _vehicle addMagazineTurret [_magazineClass, _turretPath];
        _vehicle setMagazineTurretAmmo [_magazineClass, 0, _turretPath];
    };
    if (GVAR(level) == 1) then {
        // Fill magazine completely
        _vehicle setMagazineTurretAmmo [_magazineClass, _rounds, _turretPath];
        [QEGVAR(common,displayTextStructured), [[LSTRING(Hint_RearmedTriple), _rounds,
            getText(configFile >> "CfgMagazines" >> _magazineClass >> "displayName"),
            getText(configFile >> "CfgVehicles" >> (typeOf _vehicle) >> "displayName")], 3, _unit], [_unit]] call CBA_fnc_targetEvent;
    } else {
        // Fill only at most _numRounds
        _vehicle setMagazineTurretAmmo [_magazineClass, ((_vehicle magazineTurretAmmo [_magazineClass, _turretPath]) + _numRounds) min _rounds, _turretPath];
        [QEGVAR(common,displayTextStructured), [[LSTRING(Hint_RearmedTriple), _numRounds,
            getText(configFile >> "CfgMagazines" >> _magazineClass >> "displayName"),
            getText(configFile >> "CfgVehicles" >> (typeOf _vehicle) >> "displayName")], 3, _unit], [_unit]] call CBA_fnc_targetEvent;
    };
} else {
    for "_idx" from 1 to (_maxMagazines+1) do {
        _currentRounds = _vehicle magazineTurretAmmo [_magazineClass, _turretPath];
        if (_currentRounds > 0 || {_idx == (_maxMagazines+1)}) exitWith {
            if (_idx == (_maxMagazines+1) && {!(_turretPath isEqualTo [-1])}) then {
                _vehicle addMagazineTurret [_magazineClass, _turretPath];
            };
            if (GVAR(level) == 2) then {
                //hint format ["Target: %1\nTurretPath: %2\nNumMagazines: %3\nMaxMagazines %4\nMagazine: %5\nNumRounds: %6\nMagazine: %7", _vehicle, _turretPath, _numMagazines, _maxMagazines, _currentRounds, _numRounds, _magazineClass];
                // Fill only at most _numRounds
                if ((_currentRounds + _numRounds) > _rounds) then {
                    _vehicle setMagazineTurretAmmo [_magazineClass, _rounds, _turretPath];
                    if (_numMagazines  < _maxMagazines) then {
                        _vehicle addMagazineTurret [_magazineClass, _turretPath];
                        _vehicle setMagazineTurretAmmo [_magazineClass, _currentRounds + _numRounds - _rounds, _turretPath];
                    };
                } else {
                    _vehicle setMagazineTurretAmmo [_magazineClass, _currentRounds + _numRounds, _turretPath];
                };
                [QEGVAR(common,displayTextStructured), [[LSTRING(Hint_RearmedTriple), _numRounds,
                    getText(configFile >> "CfgMagazines" >> _magazineClass >> "displayName"),
                    getText(configFile >> "CfgVehicles" >> (typeOf _vehicle) >> "displayName")], 3, _unit], [_unit]] call CBA_fnc_targetEvent;
            } else {
                // Fill current magazine completely and fill next magazine partially
                _vehicle setMagazineTurretAmmo [_magazineClass, _rounds, _turretPath];
                if (_numMagazines  < _maxMagazines) then {
                    _vehicle addMagazineTurret [_magazineClass, _turretPath];
                    _vehicle setMagazineTurretAmmo [_magazineClass, _currentRounds, _turretPath];
                };
                [QEGVAR(common,displayTextStructured), [[LSTRING(Hint_RearmedTriple), _rounds,
                    getText(configFile >> "CfgMagazines" >> _magazineClass >> "displayName"),
                    getText(configFile >> "CfgVehicles" >> (typeOf _vehicle) >> "displayName")], 3, _unit], [_unit]] call CBA_fnc_targetEvent;
            };
        };
        _vehicle removeMagazineTurret [_magazineClass, _turretPath];
        _numMagazines = _numMagazines - 1;
    };
};

/*
 * Author: GitHawk
 * Removes a magazine from the supply.
 *
 * Arguments:
 * 0: Ammo Truck <OBJECT>
 * 1: Magazine Classname <STRING>
 * 2: Number of Rounds to withdraw <NUMBER> (default: -1)
 *
 * Return Value:
 * Magazine was removed <BOOL>
 *
 * Example:
 * [ammo_truck, "500Rnd_127x99_mag_Tracer_Red"] call ace_rearm_fnc_removeMagazineFromSupply
 *
 * Public: Yes
 */
#include "script_component.hpp"

params [["_truck", objNull, [objNull]], ["_magazineClass", "", [""]], ["_numRounds", -1, [0]]];
TRACE_3("removeMagazineFromSupply",_truck,_magazineClass,_numRounds);

if (isNull _truck || {_magazineClass isEqualTo ""}) exitWith {false};

private _return = false;
([_magazineClass] call FUNC(getCaliber)) params ["_cal", "_idx"];

if (GVAR(supply) == 0) then {
    WARNING("supply setting is set to unlimited"); // func shouldn't have been called
    _return = true;
};

if (GVAR(supply) == 1) then {
    private _supply = [_truck] call FUNC(getSupplyCount);
    if (GVAR(level) == 2) then {
        // Remove partial magazine supply count
        private _rearmAmount = (REARM_COUNT select _idx);
        if (_numRounds > 0) then {
            _rearmAmount = _numRounds;
        };
        private _magazinePart = (_rearmAmount / (getNumber (configFile >> "CfgMagazines" >> _magazineClass >> "count"))) min 1;
        if (_supply >= (_cal * _magazinePart)) then {
            [_truck, (_supply - (_cal * _magazinePart))] call FUNC(setSupplyCount);
            _return = true;
        };
    } else {
        // Remove entire magazine supply count
        if (_supply >= _cal) then {
            [_truck, (_supply - _cal)] call FUNC(setSupplyCount);
            _return = true;
        };
    };
};

if (GVAR(supply) == 2) then {
    private _magazineSupply = _truck getVariable [QGVAR(magazineSupply), []];
    private _magazineIdx = -1;
    {
        _x params ["_magazine"];
        if ((_magazine isEqualTo _magazineClass)) exitWith {
            _magazineIdx = _forEachIndex;
        };
    } forEach _magazineSupply;
    if (_magazineIdx == -1) exitWith {false};

    (_magazineSupply select _magazineIdx) params ["", "_rounds"];

    private _configRounds = getNumber (configFile >> "CfgMagazines" >> _magazineClass >> "count");
    if (GVAR(level) == 2) then {
        // With caliber based rearming, we need to remove the correct amount
        private _rearmAmount = (REARM_COUNT select _idx);
        if (_numRounds > 0) then {
            if (_numRounds > _rearmAmount) then {
                _rearmAmount = ceil (_numRounds / _rearmAmount);
            };
        };
        private _roundsPerTransaction = _rearmAmount min _configRounds;
        if (_rounds >= _roundsPerTransaction) then {
            _magazineSupply set [_magazineIdx, [_magazineClass, (_rounds - _roundsPerTransaction)]];
            _truck setVariable [QGVAR(magazineSupply), _magazineSupply, true];
            _return = true;
        };
    } else {
        // Remove entire magazine
        if (_rounds >= _configRounds) then {
            _magazineSupply set [_magazineIdx, [_magazineClass, (_rounds - _configRounds)]];
            _truck setVariable [QGVAR(magazineSupply), _magazineSupply, true];
            _return = true;
        };
    };
};
_return

/*
 * Author: GitHawk
 * Sets the supply count. [Global Effects]
 *
 * Arguments:
 * 0: Ammo Truck <OBJECT>
 * 1: Supply Count <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [ammo_truck, 1000] call ace_rearm_fnc_setSupplyCount
 *
 * Public: Yes
 */
#include "script_component.hpp"

if !(EGVAR(common,settingsInitFinished)) exitWith { // only run this after the settings are initialized
    EGVAR(common,runAtSettingsInitialized) pushBack [FUNC(setSupplyCount), _this];
};

params [["_truck", objNull, [objNull]], ["_supply", 0, [0]]];

if (GVAR(supply) != 1) exitWith {WARNING("supply setting is not set to limited");};
if (isNull _truck) exitWith {WARNING_1("Truck is null [%1]",_truck);};

_truck setVariable [QGVAR(currentSupply), (_supply max 0), true];

/*
 * Author: Tuupertunut
 * Sets the ammo counts of all magazines of given class in turret.
 *
 * BIS command "setMagazineTurretAmmo" is broken at the time of writing (2017-06-24)
 * (https://feedback.bistudio.com/T79689). This function is intended as a workaround for it. All
 * magazines are removed and then added again with updated ammo counts.
 * Note: As an unintended side effect, the turret reloads after running this function.
 *
 * Arguments:
 * 0: Vehicle <OBJECT>
 * 1: Turret Path <ARRAY>
 * 2: Magazine Classname <STRING>
 * 3: Ammo Counts in Magazines <ARRAY of NUMBERs>
 *
 * Return Value:
 * None
 *
 * Example:
 * [vehicle, [0], "200Rnd_127x99_mag_Tracer_Red", [200, 152]] call ace_rearm_fnc_setTurretMagazineAmmo
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_vehicle", "_turretPath", "_magazineClass", "_ammoCounts"];

// Checking if a magazine of given class is currently loaded in any weapon.
private _magLoadedInWeapon = false;
private _loadedWeapon = "";
{
    private _currentlyLoadedMag = (weaponState [_vehicle, _turretPath, _x]) select 3;

    if (_currentlyLoadedMag isEqualTo _magazineClass) exitWith {
        _magLoadedInWeapon = true;
        _loadedWeapon = _x;
    };
} forEach (_vehicle weaponsTurret _turretPath);


if (!_magLoadedInWeapon) then {
    /* The easy case:
     * The magazine class was not loaded, so we can just remove those magazines and
     * add them back with updated ammo counts. */

    _vehicle removeMagazinesTurret [_magazineClass, _turretPath];
    {
        _vehicle addMagazineTurret [_magazineClass, _turretPath, _x];
    } forEach _ammoCounts;

} else {
    /* Special hack case:
     * The magazine class was loaded into a weapon. If the weapon has more than one type of
     * magazine (e.g. AP and HEAT in a cannon), then removing all magazines would trigger the
     * weapon to load a different magazine type. For example, removing the HEAT shells while HEAT
     * is loaded makes the cannon switch to AP.
     * 
     * To prevent that, we must remove all magazines that would fit into the weapon and then add
     * them back with the magazine-to-be-loaded being the first. */

    private _allowedMagClassesInWeapon = getArray (configFile >> "CfgWeapons" >> _loadedWeapon >> "magazines");

    /* Current ammo counts of all allowed magazine classes in weapon.
     * Example: [["8Rnd_82mm_Mo_shells", [8, 8, 2]], ["8Rnd_82mm_Mo_Flare_white", [7]]] */
    private _ammoCountsByMagClass = _allowedMagClassesInWeapon apply {[_x, ([_vehicle, _turretPath, _x] call FUNC(getTurretMagazineAmmo))]};

    // Removing all magazines that fit into the weapon.
    {
        _vehicle removeMagazinesTurret [_x, _turretPath];
    } forEach _allowedMagClassesInWeapon;

    // Adding the mags of the given class first with updated ammo counts.
    {
        _vehicle addMagazineTurret [_magazineClass, _turretPath, _x];
    } forEach _ammoCounts;

    // Adding back all other magazines with their original ammo counts.
    {
        _x params ["_loopMagClass", "_loopAmmoCounts"];

        if (!(_loopMagClass isEqualTo _magazineClass)) then {
            {
                _vehicle addMagazineTurret [_loopMagClass, _turretPath, _x];
            } forEach _loopAmmoCounts;
        };
    } forEach _ammoCountsByMagClass;
};

TRACE_5("setTurretMagazineAmmo",_vehicle,_turretPath,_magazineClass,_ammoCounts,_loadedWeapon);

/*
 * Author: GitHawk
 * Stores ammo in an ammo truck.
 *
 * Arguments:
 * 0: Ammo Truck <OBJECT>
 * 1: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
