// Find dependent hitpoints
{
    if ((_x != "") && {isClass (_config >> _x)} && {!(_x in _realHitPoints)}) then {
        _realHitPoints pushBack _x;
        if (!((getText (_config >> _x >> "depends")) in ["", "0"])) then {
            _dependentHitPoints pushBack _x;
