/*
 * Author: commy2
 * Animate scripted reticle of spotting scope.
 *
 * Arguments:
 * 0: Reticles RSC info display <DISPLAY>
 *
 * Return Value:
 * None
 *
 * Example:
 * [DISPLAY] call ace_spottingscope_fnc_animateReticle
 *
 * Public: No
 */
#include "script_component.hpp"

disableSerialization;

params ["_display"];
uinamespace setVariable [QGVAR(dlgSpottingScope), _display];

private _ctrlReticle = _display displayCtrl IDC_RETICLE;
private _ctrlBody = _display displayCtrl IDC_BODY;
private _ctrlBlackLeft = _display displayCtrl IDC_BLACK_LEFT;
private _ctrlBlackRight = _display displayCtrl IDC_BLACK_RIGHT;

// check if optics are used
// hide all controls otherwise
private _isUsingOptic = ctrlShown (_display displayCtrl 154);

_ctrlReticle ctrlShow _isUsingOptic;
_ctrlBody ctrlShow _isUsingOptic;
_ctrlBlackLeft ctrlShow _isUsingOptic;
_ctrlBlackRight ctrlShow _isUsingOptic;

// animate reticle
private _zoom = ([] call EFUNC(common,getZoom)) * MAGIC_SCOPE_NUMBER;

_ctrlReticle ctrlSetPosition [
    POS_X_BASE(_zoom),
    POS_Y_BASE(_zoom),
    POS_W_BASE(_zoom),
    POS_H_BASE(_zoom)
];

_ctrlReticle ctrlCommit 0;

/*
 * Author: Rocko, Ruthberg
 * Pick up spotting scope
 *
 * Arguments:
 * 0: spotting scope <OBJECT>
 * 1: unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [spotting_scope, player] call ace_spottingscope_fnc_pickup
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_spottingScope", "_unit"];

if ((_unit call CBA_fnc_getUnitAnim) select 0 == "stand") then {
    _unit playMove "AmovPercMstpSrasWrflDnon_diary";
};

[{
    params ["_spottingScope", "_unit"];

    [_unit, "ACE_SpottingScope"] call EFUNC(common,addToInventory);
    deleteVehicle _spottingScope;
}, [_spottingScope, _unit], 1, 0]call CBA_fnc_waitAndExecute;

/*
 * Author: Rocko, Ruthberg
 * Place down spotting scope
 *
 * Arguments:
 * 0: unit <OBJECT>
 * 1: scope class <STRING>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, "ACE_SpottingScope"] call ace_spottingscope_fnc_place
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_scopeClass"];

_unit removeItem _scopeClass;

if ((_unit call CBA_fnc_getUnitAnim) select 0 == "stand") then {
    _unit playMove "AmovPercMstpSrasWrflDnon_diary";
};

[{
    params ["_unit"];

    // prevent collision damage
    [QEGVAR(common,fixCollision), _unit] call CBA_fnc_localEvent;

    // Check for a place to land the spotting scope
    private _direction = getDir _unit;
    private _position = (getPosASL _unit) vectorAdd [0.8 * sin(_direction), 0.8 * cos(_direction), 0];
    private _vectorUp = [0, 0, 1];
    private _intersections = lineIntersectsSurfaces [_position vectorAdd [0, 0, 1.5], _position vectorDiff [0, 0, 1.5], _unit, objNull, true, 1, "GEOM", "FIRE"];
    if (_intersections isEqualTo []) then {
        TRACE_1("No intersections",_intersections);
    } else {
        (_intersections select 0) params ["_touchingPoint", "_surfaceNormal"];
        _position = _touchingPoint vectorAdd [0, 0, 0.05];
        _vectorUp = _surfaceNormal;
    };

    // Create the scope and set its position and orientation
    private _spottingScope = "ACE_SpottingScopeObject" createVehicle [0, 0, 0];
    _spottingScope setDir _direction;
    _spottingScope setPosASL _position;
    _spottingScope setVectorUp _vectorUp;
    [QEGVAR(common,fixPosition), _spottingScope, _spottingScope] call CBA_fnc_targetEvent;
    [QEGVAR(common,fixFloating), _spottingScope, _spottingScope] call CBA_fnc_targetEvent;

    _unit reveal _spottingScope;

}, [_unit], 1, 0] call CBA_fnc_waitAndExecute;

