/*
 * Author: Rocko, Ruthberg, commy2
 * Cancel tactical ladder deployment
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Key <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [_ladder] call ace_tacticalladder_fnc_cancelTLdeploy
 *
 * Public: No
 */
#include "script_component.hpp"

#define __ANIMS ["extract_1","extract_2","extract_3","extract_4","extract_5","extract_6","extract_7","extract_8","extract_9","extract_10","extract_11"]

params ["_unit", "_key"];

if (_key != 1 || {isNull GVAR(ladder)}) exitWith {};

// enable running again
[_unit, "forceWalk", "ACE_Ladder", false] call EFUNC(common,statusEffect_set);

detach GVAR(ladder);

GVAR(ladder) animate ["rotate", 0];

{
    GVAR(ladder) animate [_x, 0];
    true
} count __ANIMS;

// remove mouse buttons and hint
call EFUNC(interaction,hideMouseHint);

[_unit, "DefaultAction", _unit getVariable [QGVAR(Deploy), -1]] call EFUNC(Common,removeActionEventHandler);

GVAR(ladder) = objNull;

/*
 * Author: Rocko, Ruthberg, commy2
 * Confirm tactical ladder deployment
 *
 * Arguments:
 * 0: unit <OBJECT>
 * 1: ladder <OBJECT>
 *
 * Return Value:
 * Success <BOOL>
 *
 * Example:
 * [_ladder] call ace_tacticalladder_fnc_confirmTLdeploy
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_ladder"];

// enable running again
[_unit, "forceWalk", "ACE_Ladder", false] call EFUNC(common,statusEffect_set);

private _pos1 = getPosASL _ladder;
private _pos2 = AGLToASL (_ladder modelToWorld (_ladder selectionPosition "check2"));

if (lineIntersects [_pos1, _pos2, _ladder]) exitWith {false};

detach _ladder;

// remove mouse buttons and hint
call EFUNC(interaction,hideMouseHint);

[_unit, "DefaultAction", _unit getVariable [QGVAR(Deploy), -1]] call EFUNC(common,removeActionEventHandler);

GVAR(ladder) = objNull;

true

/*
 * Author: Rocko, Ruthberg
 * Deploy tactical ladder
 *
 * Arguments:
 * 0: unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [_unit] call ace_tacticalladder_fnc_deployTL
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];

if (backpack _unit != 'ACE_TacticalLadder_Pack') exitWith {};

removeBackpack _unit;


private _pos = _unit modelToWorld [0,0,0];
private _offset = if ((_unit call CBA_fnc_getUnitAnim select 0) == "prone") then { 1 } else {0.8};

_pos set [0, (_pos select 0) + (sin getDir _unit) * _offset];
_pos set [1, (_pos select 1) + (cos getDir _unit) * _offset];
_pos set [2, [_unit] call CBA_fnc_realHeight];

private _ladder = "ACE_TacticalLadder" createVehicle _pos;
_ladder setPos _pos;
_ladder setDir getDir _unit;

_unit reveal _ladder;

/*
 * Author: commy2
 * Handle opening of interaction menu.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [bob] call ace_tacticalladder_fnc_handleInteractMenuOpened
 *
 * Public: No
*/
#include "script_component.hpp"

params ["_unit"];

if (!isNull (GETMVAR(GVAR(ladder),objNull)) && {GVAR(ladder) in attachedObjects _unit}) then {
    [_unit, GVAR(ladder)] call FUNC(cancelTLdeploy);
};

/*
 * Author: commy2
 * Handle death.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [bob] call ace_tacticalladder_fnc_handleKilled
 *
 * Public: No
*/
#include "script_component.hpp"

params ["_unit"];

if (!isNull (GETMVAR(ladder,objNull)) && {GVAR(ladder) in attachedObjects _unit}) then {
    [_unit, GVAR(ladder)] call FUNC(cancelTLdeploy);
};

/*
 * Author: commy2
 * Handle player changes.
 *
 * Arguments:
 * 0: New Player Unit <OBJECT>
 * 1: Old Player Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [bob, kevin] call ace_tacticalladder_fnc_handlePlayerChanged
 *
 * Public: No
*/
#include "script_component.hpp"

if (isNull (GETGVAR(ladder,objNull))) exitWith {};

params ["_newPlayer", "_oldPlayer"];

if (GVAR(ladder) in attachedObjects _newPlayer) then {
    [_newPlayer, GVAR(ladder)] call FUNC(cancelTLdeploy);
};

if (GVAR(ladder) in attachedObjects _oldPlayer) then {
    [_oldPlayer, GVAR(ladder)] call FUNC(cancelTLdeploy);
};

/*
 * Author: Rocko, Ruthberg
 * Handles lengthening and tilting of the ladder
 *
 * Arguments:
 * Amount scrolled <NUMBER>
 *
 * Return Value:
 * Handled <BOOL>
 *
 * Example:
 * [1] call ace_tacticalladder_fnc_handleScrollWheel;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_scroll"];

if (isNull GVAR(ladder)) exitWith { false };

if (ACE_Modifier == 0) then {
    // Lengthening
    if (_scroll > 0) then {
        private _currentStep = GVAR(currentStep);
        if (_currentStep == 11) exitWith {};
        _currentStep = _currentStep + 1;
        if (GVAR(ladder) animationPhase (format["extract_%1", _currentStep]) == 0) then {
            GVAR(ladder) animate [format["extract_%1", _currentStep], 1];
            GVAR(currentStep) = _currentStep;
        };
    };
    if (_scroll < 0) then {
        private _currentStep = GVAR(currentStep);
        if (_currentStep == 3) exitWith {};
        if (GVAR(ladder) animationPhase (format["extract_%1", _currentStep]) == 1) then {
            GVAR(ladder) animate [format["extract_%1", _currentStep], 0];
            GVAR(currentStep) = _currentStep - 1;
        };
    };
};// else {
    // Tilting (disabled due to sinking, interaction point offset and unsuitable animation)
    //GVAR(currentAngle) = 0 max (GVAR(currentAngle) + _scroll) min 30;
    //GVAR(ladder) animate ["rotate", GVAR(currentAngle)];
//};

true

/*
 * Author: commy2
 * Handle unconsciousness.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [bob] call ace_tacticalladder_fnc_handleUnconscious
 *
 * Public: No
*/
#include "script_component.hpp"

params ["_unit"];

if (!local _unit) exitWith {};

if (!isNull (GETMVAR(ladder,objNull)) && {GVAR(ladder) in attachedObjects _unit}) then {
    [_unit, GVAR(ladder)] call FUNC(cancelTLdeploy);
};

/*
 * Author: Jonpas
 * Checks if Tactical Ladder is empty (no one climbing it).
 *
 * Arguments:
 * 0: Ladder <OBJECT>
 *
 * Return Value:
 * Can Be Picked Up <BOOL>
 *
 * Example:
 * [ladder] call ace_tacticalladder_fnc_isLadderEmpty;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_ladder"];

private _unitsNearLadder = nearestObjects [_ladder, ["CAManBase"], 4];
TRACE_1("Near Ladder",_unitsNearLadder);

private _ladderEmpty = true;
{
    if (getNumber (configFile >> "CfgMovesMaleSdr" >> "States" >> animationState _x >> "ACE_isLadder") == 1) exitWith {
        _ladderEmpty = false;
        TRACE_1("On Ladder",_x);
    };
} forEach _unitsNearLadder;

_ladderEmpty

/*
 * Author: Rocko, Ruthberg
 * Position tactical ladder
 *
 * Arguments:
 * 0: unit <OBJECT>
 * 1: ladder <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [_unit, _ladder] call ace_tacticalladder_fnc_positionTL
 *
 * Public: No
 */
#include "script_component.hpp"

#define __ANIMS ["extract_1","extract_2","extract_3","extract_4","extract_5","extract_6","extract_7","extract_8","extract_9","extract_10","extract_11"]

params ["_unit", "_ladder"];

// prevent the placing unit from running
[_unit, "forceWalk", "ACE_Ladder", true] call EFUNC(common,statusEffect_set);

{
    _ladder animate [_x, 0];
} count __ANIMS;

[_unit, "amovpercmstpslowwrfldnon_player_idlesteady03", 2] call EFUNC(common,doAnimation);

_ladder attachTo [_unit, [0, 0.75, 0], ""]; // Position ladder in front of player

_ladder animate ["rotate", 0];
{
    _ladder animate [_x, 1];
} count ["extract_1", "extract_2", "extract_3"]; // Extract ladder at head height (extract_3)

GVAR(ladder) = _ladder;
GVAR(cancelTime) = CBA_missionTime + 1; // Workaround to prevent accidental canceling
GVAR(currentStep) = 3;
GVAR(currentAngle) = 0;

// add mouse buttons and hints
//private _adjustText = format ["%1, +%2", localize LSTRING(Adjust), localize LSTRING(AdjustTilt)]; // Tilting disabled due to sinking, interaction point offset and unsuitable animation
[localize LSTRING(Deploy), localize LSTRING(Drop), /*_adjustText*/ localize LSTRING(Adjust)] call EFUNC(interaction,showMouseHint);

_unit setVariable [QGVAR(Deploy), [
    _unit, "DefaultAction",
    {!isNull GVAR(ladder)},
    {[_this select 0, GVAR(ladder)] call FUNC(confirmTLdeploy)}
] call EFUNC(common,addActionEventHandler)];

