/*
 * Author: Jonpas
 * Adds custom tag. Has to be executed on one machine only.
 *
 * Arguments:
 * 0: Unique Identifier <STRING>
 * 1: Display Name <STRING>
 * 2: Required Item <STRING>
 * 3: Textures Paths <ARRAY>
 * 4: Icon Path <STRING> (default: "")
 *
 * Return Value:
 * Sucessfully Added Tag <BOOL>
 *
 * Example:
 * ["ace_victoryRed", "Victory Red", "ACE_SpraypaintRed", ["path\to\texture1.paa", "path\to\texture2.paa"], "path\to\icon.paa"] call ace_tagging_fnc_addCustomTag
 *
 * Public: Yes
 */
#include "script_component.hpp"

params [
    ["_identifier", "", [""]],
    ["_displayName", "", [""]],
    ["_requiredItem", "", [""]],
    ["_textures", [], [[]]],
    ["_icon", "", [""]]
];

// Verify
if (_identifier == "") exitWith {
    ERROR("Failed adding custom tag - missing identifier");
};

if (_displayName == "") exitWith {
    ERROR_1("Failed adding custom tag: %1 - missing displayName",_identifier);
};

if (_requiredItem == "") exitWith {
    ERROR_1("Failed adding custom tag: %1 - missing requiredItem",_identifier);
};
if (!isClass (configFile >> "CfgWeapons" >> _requiredItem)) exitWith {
    ERROR_2("Failed adding custom tag: %1 - requiredItem %2 does not exist",_identifier,_requiredItem);
};

if (_textures isEqualTo []) exitWith {
    ERROR_1("Failed adding custom tag: %1 - missing textures",_identifier);
};

_identifier = [_identifier] call CBA_fnc_removeWhitespace;
_requiredItem = toLower _requiredItem;

// Add
[QGVAR(applyCustomTag), [_identifier, _displayName, _requiredItem, _textures, _icon]] call CBA_fnc_globalEventJIP;

/*
 * Author: Jonpas
 * Compiles tags from ACE_Tags and returns children actions.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [unit] call ace_tagging_fnc_addTagActions
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];

private _actions = [];
{
    _x params ["_class", "_displayName", "_requiredItem", "_textures", "_icon"];

    _actions pushBack [
        [
            format ["ACE_ConfigTag_%1", _class],
            _displayName,
            _icon,
            {
                (_this select 2) params ["_unit", "_class", "_textures"];
                [_unit, selectRandom _textures] call FUNC(tag);
                _unit setVariable [QGVAR(lastUsedTag), _class];
            },
            {
                (_this select 2) params ["_unit", "", "", "_requiredItem"];
                _requiredItem in ((items _unit) apply {toLower _x})
            },
            {},
            [_unit, _class, _textures, _requiredItem]
        ] call EFUNC(interact_menu,createAction),
        [],
        _unit
    ];
} forEach GVAR(cachedTags);

_actions

/*
 * Author: Jonpas
 * Applies custom tag to the cache.
 *
 * Arguments:
 * 0: Unique Identifier <STRING>
 * 1: Display Name <STRING>
 * 2: Required Item <STRING>
 * 3: Textures Paths <ARRAY>
 * 4: Icon Path <STRING> (default: "")
 *
 * Return Value:
 * None
 *
 * Example:
 * ["ace_victoryRed", "Victory Red", "ACE_SpraypaintRed", ["path\to\texture1.paa", "path\to\texture2.paa"], "path\to\icon.paa"] call ace_tagging_fnc_applyCustomTag
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_identifier", "_displayName", "_requiredItem"];

// Add only if tag not already added (compare identifiers)
if !(GVAR(cachedTags) select {_x select 0 == _identifier} isEqualTo []) exitWith {
    INFO_2("Tag with selected identifier already exists: %1 (%2)",_identifier,_displayName)
};

GVAR(cachedTags) pushBack _this;
GVAR(cachedRequiredItems) pushBackUnique _requiredItem;
TRACE_1("Added custom script tag",_this);

/*
 * Author: BaerMitUmlaut, esteldunedain
 * Checks if there is a taggable surface within 2.5m in front of the player.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * Is surface taggable <BOOL>
 *
 * Example:
 * [player] call ace_tagging_fnc_checkTaggable
 *
 * Public: No
 */

#include "script_component.hpp"

[_this, {
    params ["_unit"];

    // Exit if no required item in inventory
    if ((GVAR(cachedRequiredItems) arrayIntersect ((items _unit) apply {toLower _x})) isEqualTo []) exitWith {false};

    private _startPosASL = eyePos _unit;
    private _cameraPosASL =  AGLToASL positionCameraToWorld [0, 0, 0];
    private _cameraDir = (AGLToASL positionCameraToWorld [0, 0, 1]) vectorDiff _cameraPosASL;
    private _endPosASL = _startPosASL vectorAdd (_cameraDir vectorMultiply 2.5);

    private _intersections = lineIntersectsSurfaces [_startPosASL, _endPosASL, _unit, objNull, true, 1, "FIRE", "GEOM"];

    // If there's no intersections
    if (_intersections isEqualTo []) exitWith {false};

    (_intersections select 0) params ["", "", "", "_object"];

    // Exit if trying to tag a non static object
    TRACE_1("Obj:",_intersections);

    // Exit if trying to tag a non static object
    if ((!isNull _object) && {
        // If the class is alright, do not exit
        if (_object isKindOf "Static") exitWith {false};

        // If the class is not categorized correctly search the cache
        private _modelName = (getModelInfo _object) select 0;
        private _isStatic = GVAR(cacheStaticModels) getVariable [_modelName, false];
        TRACE_2("Object:",_modelName,_isStatic);
        // If the class in not on the cache, exit
        (!_isStatic)
    }) exitWith {
        TRACE_1("Pointed object is non static",_object);
        false
    };

    true
}, missionNamespace, QGVAR(checkTaggableCache), 0.5] call EFUNC(common,cachedCall);

/*
 * Author: Jonpas
 * Compiles and caches tags from ACE_Tags config.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_tagging_fnc_compileConfigTags
 *
 * Public: No
 */
#include "script_component.hpp"

{
    private _failure = false;
    private _class = configName _x;

    private _displayName = getText (_x >> "displayName");
    if (_displayName == "") then {
        ERROR_1("Failed compiling ACE_Tags for tag: %1 - missing displayName",_class);
        _failure = true;
    };

    private _requiredItem = toLower (getText (_x >> "requiredItem"));
    if (_requiredItem == "") then {
        ERROR_1("Failed compiling ACE_Tags for tag: %1 - missing requiredItem",_class);
        _failure = true;
    } else {
        if (!isClass (configFile >> "CfgWeapons" >> _requiredItem)) then {
            ERROR_2("Failed compiling ACE_Tags for tag: %1 - requiredItem %2 does not exist",_class,_requiredItem);
            _failure = true;
        };
    };

    private _textures = getArray (_x >> "textures");
    if (_textures isEqualTo []) then {
        ERROR_1("Failed compiling ACE_Tags for tag: %1 - missing textures",_class);
        _failure = true;
    };

    private _icon = getText (_x >> "icon");

    if (!_failure) then {
        GVAR(cachedTags) pushBack [_class, _displayName, _requiredItem, _textures, _icon];
        GVAR(cachedRequiredItems) pushBackUnique _requiredItem;
    };
} forEach ("true" configClasses (configFile >> "ACE_Tags"));

/*
 * Author: BaerMitUmlaut, esteldunedain
 * Creates a tag and handle its destruction. Only execute on the server.
 *
 * Arguments:
 * 0: Position ASL <ARRAY>
 * 1: Vector dir and up <ARRAY>
 * 2: Colour of the tag (valid colours are black, red, green and blue or full path to custom texture) <STRING>
 * 3: Object it should be tied to <OBJECT>
 * 4: Unit that created the tag <OBJECT>
 *
 * Return Value:
 * Tag created <BOOL>
 *
 * Example:
 * [positionASL, vectorDirAndUp, "z\ace\addons\tagging\UI\tags\black\0.paa", object] call ace_tagging_fnc_createTag
 *
 * Public: No
 */

#include "script_component.hpp"

params ["_tagPosASL", "_vectorDirAndUp", "_texture", "_object", "_unit"];
TRACE_5("createTag:",_tagPosASL,_vectorDirAndUp,_texture,_object,_unit);

if (_texture == "") exitWith {
    ERROR_1("%1 is not a valid tag texture.",_texture);
    false
};

private _tag = createSimpleObject ["UserTexture1m_F", _tagPosASL];
_tag setObjectTextureGlobal [0, _texture];
_tag setVectorDirAndUp _vectorDirAndUp;

// Throw a global event for mision makers
["ace_tagCreated", [_tag, _texture, _object, _unit]] call CBA_fnc_globalEvent;

if (isNull _object) exitWith {true};

// If the tag is applied to an object, handle its destruction
_object setVariable [QGVAR(testVar), true];
if (_object getVariable [QGVAR(testVar), false]) then {
    // The object supports variables and hence HandleDamage too
    // Use the cheaper alternative for handling destruction: HandleDamage

    // If the object already has tags attached, just add the new one to the list
    private _attachedTags = _object getVariable QGVAR(attachedTags);
    if !(isNil  "_attachedTags ") exitWith {
        _attachedTags pushBack _tag;
    };

    _attachedTags = [_tag];
    _object setVariable [QGVAR(attachedTags), _attachedTags];

    // If it's the first tag attached to that object, add a handledamage event handler
    _object addEventHandler ["HandleDamage", {
        params ["_object", "_selection", "_damage"];
        if (_selection == "" && _damage >= 1) then {
            {
                deleteVehicle _x;
            } foreach (_object getVariable [QGVAR(attachedTags), []]);
            _object setVariable [QGVAR(attachedTags), []];
        };
    }];
};

// Periodic test the tag:
// - For object that don't support handledamage
// - For object that do, to support doors opening/closing, etc
GVAR(tagsToTest) pushBack [_tag, _tagPosASL, _vectorDirAndUp];

// Run the test
if (!GVAR(testingThread)) then {
    call FUNC(tagTestingThread);
};

true

/*
 * Author: Jonpas
 * Selects random tag and applies it.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player] call ace_tagging_fnc_quickTag
 *
 * Public: No
 */

#include "script_component.hpp"

// Exit if Quick Tag disabled
if (GVAR(quickTag) == 0) exitWith {};

params ["_unit"];

private _possibleTags = [];
private _useRandom = false;

// Last Used
if (GVAR(quickTag) == 1) then {
    private _lastUsedTagClass = _unit getVariable [QGVAR(lastUsedTag), nil];

    if (isNil "_lastUsedTagClass") then {
        _useRandom = true;
    } else {
        private _lastUsedTag = GVAR(cachedTags) select {(_x select 0) == _lastUsedTagClass};
        _possibleTags = _lastUsedTag;
    };
};

// Random X
if ((GVAR(quickTag) == 2) || _useRandom) then {
    private _xTags = GVAR(cachedTags) select {(_x select 0) in ["ACE_XBlack", "ACE_XRed", "ACE_XGreen", "ACE_XBlue"]};
    _possibleTags = _xTags;
};

// Random
if (GVAR(quickTag) == 3) then {
    _possibleTags = GVAR(cachedTags);
};

// Tag
if !(_possibleTags isEqualTo []) then {
    private _availableTags = _possibleTags select {(_x select 2) in ((items _unit) apply {toLower _x})};
    [_unit, selectRandom ((selectRandom _availableTags) select 3)] call FUNC(tag);
};

/*
 * Author: BaerMitUmlaut, esteldunedain
 * Creates a tag on a wall that is on the closest surface within 2m on front of the unit.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: The colour of the tag (valid colours are black, red, green and blue or full path to custom texture) <STRING>
 *
 * Return Value:
 * Sucess <BOOL>
 *
 * Example:
 * success = [player, "z\ace\addons\tagging\UI\tags\black\0.paa"] call ace_tagging_fnc_tag
 *
 * Public: Yes
 */

#include "script_component.hpp"

params [
    ["_unit", objNull, [objNull]],
    ["_texture", "", [""]]
];

if (isNull _unit || {_texture == ""}) exitWith {
    ERROR_2("Tag parameters invalid. Unit: %1, Texture: %2",_unit,_texture);
};

private _startPosASL = eyePos _unit;
private _cameraPosASL =  AGLToASL positionCameraToWorld [0, 0, 0];
private _cameraDir = (AGLToASL positionCameraToWorld [0, 0, 1]) vectorDiff _cameraPosASL;
private _endPosASL = _startPosASL vectorAdd (_cameraDir vectorMultiply 2.5);

// Check for intersections below the unit
private _intersections = lineIntersectsSurfaces [_startPosASL, _endPosASL, _unit, objNull, true, 1, "GEOM", "FIRE"];

// If there's no intersections
if (_intersections isEqualTo []) exitWith {
    TRACE_3("No intersections",_intersections);
    false
};

(_intersections select 0) params ["_touchingPoint", "_surfaceNormal", "", "_object"];
TRACE_3("",_touchingPoint, _surfaceNormal, _object);

// Exit if trying to tag a non static object
if ((!isNull _object) && {
    // If the class is alright, do not exit
    if (_object isKindOf "Static") exitWith {false};

    // If the class is not categorized correctly search the cache
    private _modelName = (getModelInfo _object) select 0;
    private _isStatic = GVAR(cacheStaticModels) getVariable [_modelName, false];
    TRACE_2("Object:",_modelName,_isStatic);
    // If the class in not on the cache, exit
    (!_isStatic)
}) exitWith {
    TRACE_1("Pointed object is non static",_object);
    false
};


// If the surface normal points away, flip it. This happens in weird places like the Stratis Pier
if (_surfaceNormal vectorDotProduct  (_endPosASL vectorDiff _startPosASL) > 0) then {
    _surfaceNormal = _surfaceNormal vectorMultiply -1;
};

// Check if its a valid surface: big enough, reasonably plane
private _v1 = vectorNormalized (_surfaceNormal vectorMultiply -1);
private _v2 = vectorNormalized (_v1 vectorCrossProduct (_endPosASL vectorDiff _startPosASL));
// If the surface is not horizontal (>20Âº), create vup _v2 pointing upward instead of away
if (abs (_v1 select 2) < 0.94) then {
    private _v3Temp = _v1 vectorCrossProduct [0, 0, 1];
    _v2 = _v3Temp vectorCrossProduct _v1;
};
private _v3 = _v2 vectorCrossProduct _v1;

TRACE_3("Reference:", _v1, _v2, _v3);

private _fnc_isOk = {
    params ["_rx", "_ry"];
    private _startPosASL2 = _touchingPoint vectorAdd (_v2 vectorMultiply _rx) vectorAdd (_v3 vectorMultiply _ry) vectorAdd (_v1 vectorMultiply (-0.06));
    private _endPosASL2   = _startPosASL2 vectorAdd (_v1 vectorMultiply (0.12));
    private _intersections = lineIntersectsSurfaces [_startPosASL2, _endPosASL2, _unit, objNull, true, 1, "GEOM", "FIRE"];
    // If there's no intersections
    if (_intersections isEqualTo []) exitWith {false;};

    if !(((_intersections select 0) select 3) isEqualTo _object) exitWith {false;};

    true
};

if ( !([ 0.5 * TAG_SIZE, 0.5 * TAG_SIZE] call _fnc_isOk) ||
    {!([ 0.5 * TAG_SIZE,-0.5 * TAG_SIZE] call _fnc_isOk) ||
    {!([-0.5 * TAG_SIZE, 0.5 * TAG_SIZE] call _fnc_isOk) ||
    {!([-0.5 * TAG_SIZE,-0.5 * TAG_SIZE] call _fnc_isOk)}}}) exitWith {
    TRACE_3("Unsuitable location:",_touchingPoint);
    false
};

private _vectorDirAndUp = [_surfaceNormal vectorMultiply -1, _v3];

// Everything ok, make the unit create the tag
[_unit, "PutDown"] call EFUNC(common,doGesture);

[{
    params ["", "", "", "", "_unit"];
    TRACE_2("Unit:",_unit,_this);

    playSound3D [QUOTE(PATHTO_R(sounds\spray.ogg)), _unit, false, (eyePos _unit), 10, 1, 15];

    // Tell the server to create the tag and handle its destruction
    [QGVAR(createTag), _this] call CBA_fnc_serverEvent;
}, [_touchingPoint vectorAdd (_surfaceNormal vectorMultiply 0.06), _vectorDirAndUp, _texture, _object, _unit], 0.6] call CBA_fnc_waitAndExecute;

true

/*
 * Author: esteldunedain
 * Checks if tags are still leaning on an object periodically.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_tagging_fnc_tagTestingThread
 *
 * Public: No
 */

#include "script_component.hpp"

GVAR(tagsToTest) = GVAR(tagsToTest) select {
    _x params ["_tag", "_tagPosASL", "_vectorDirAndUp"];

    _vectorDirAndUp params ["_v1", "_v2"];

    private _endPosASL = _tagPosASL vectorAdd (_v1 vectorMultiply 0.08);

    // Check for intersections below the unit
    private _intersections = lineIntersectsSurfaces [_tagPosASL, _endPosASL, _tag, objNull, true, 1, "GEOM", "FIRE"];

    // If there's no intersections
    if (_intersections isEqualTo []) then {
        TRACE_1("No intersections, deleting:",_tag);
        deleteVehicle _tag;
        false
    } else {
        true
    };
};

// If there's no more tag
if (GVAR(tagsToTest) isEqualTo []) exitWith {
    GVAR(testingThread) = false;
};

// Schedule for execution again after 5 seconds
[DFUNC(tagTestingThread), [], 5] call CBA_fnc_waitAndExecute;
GVAR(testingThread) = true;

