/*
 * Author: Jonpas
 * Compiles and caches UI from ACE_UI config.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_ui_fnc_compileConfigUI
 *
 * Public: No
 */
#include "script_component.hpp"

{
    private _failure = false;
    private _class = toLower (configName _x);

    private _idd = getNumber (_x >> "idd");

    private _elements = getArray (_x >> "elements");
    if (_elements isEqualTo []) then {
        ERROR_1("Failed compiling ACE_UI for Element: %1 - missing elements",_class);
        _failure = true;
    };

    private _location = getNumber (_x >> "location");
    if !(_location in [ANYWHERE, GROUND_ONLY, VEHICLE_ONLY]) then {
        ERROR_2("Failed compiling ACE_UI for Element: %1 - missing or invalid location %2",_class,_location);
        _failure = true;
    };

    if (!_failure) then {
        private _conditions = [];
        {
            _conditions pushBack [compile (getText _x), configName _x];
            TRACE_1("Caching Condition",_x);
        } forEach (configProperties [_x >> "conditions"]);

        GVAR(configCache) setVariable [_class, [_idd, _elements, _location, _conditions]];
    };
} forEach ("true" configClasses (configFile >> "ACE_UI"));

/*
 * Author: Jonpas
 * Initializes the UI module.
 *
 * Arguments:
 * 0: Module Logic <LOGIC>
 * 1: Units <ARRAY>
 * 2: Activated <BOOL>
 *
 * Return Value:
 * None
 *
 * Example:
 * [LOGIC, [bob, kevin], true] call ace_ui_fnc_moduleInit
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_logic", "_units", "_activated"];

if (!_activated) exitWith {};

[_logic, QGVAR(allowSelectiveUI), "allowSelectiveUI"] call EFUNC(common,readSettingFromModule);

// Basic
if (isArray (missionConfigFile >> "showHUD")) then {
    // HUD visibility is hardcoded in mission config and showHUD command is overriden
    INFO("User Interface Module Failed to Initialize Basic settings - showHUD overriden in mission config!");
} else {
    [_logic, QGVAR(soldierVehicleWeaponInfo), "soldierVehicleWeaponInfo"] call EFUNC(common,readSettingFromModule);
    [_logic, QGVAR(vehicleRadar), "vehicleRadar"] call EFUNC(common,readSettingFromModule);
    [_logic, QGVAR(vehicleCompass), "vehicleCompass"] call EFUNC(common,readSettingFromModule);
    [_logic, QGVAR(commandMenu), "commandMenu"] call EFUNC(common,readSettingFromModule);
    [_logic, QGVAR(groupBar), "groupBar"] call EFUNC(common,readSettingFromModule);
};

// Advanced
[_logic, QGVAR(weaponName), "weaponName"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(weaponNameBackground), "weaponNameBackground"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(firingMode), "firingMode"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(ammoType), "ammoType"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(ammoCount), "ammoCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(magCount), "magCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(throwableName), "throwableName"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(throwableCount), "throwableCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(zeroing), "zeroing"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(weaponLowerInfoBackground), "weaponLowerInfoBackground"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(stance), "stance"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(staminaBar), "staminaBar"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerWeaponName), "gunnerWeaponName"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerWeaponNameBackground), "gunnerWeaponNameBackground"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerFiringMode), "gunnerFiringMode"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerAmmoType), "gunnerAmmoType"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerAmmoCount), "gunnerAmmoCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerMagCount), "gunnerMagCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerLaunchableName), "gunnerLaunchableName"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerLaunchableCount), "gunnerLaunchableCount"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerZeroing), "gunnerZeroing"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(gunnerWeaponLowerInfoBackground), "gunnerWeaponLowerInfoBackground"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleName), "vehicleName"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleNameBackground), "vehicleNameBackground"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleFuelBar), "vehicleFuelBar"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleSpeed), "vehicleSpeed"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleAltitude), "vehicleAltitude"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleDamage), "vehicleDamage"] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(vehicleInfoBackground), "vehicleInfoBackground"] call EFUNC(common,readSettingFromModule);

INFO_1("User Interface Module Initialized. Allow client modifications: %1",GVAR(allowSelectiveUI));

/*
 * Author: Jonpas
 * Sets advanced visible element of the UI using displays and controls.
 *
 * Arguments:
 * 0: Element Name <STRING>
 * 1: Show/Hide Element <BOOL>
 * 2: Show Hint <BOOL>
 * 3: Force change even when disallowed <BOOL> (default: false)
 *
 * Return Value:
 * Successfully Set <BOOL>
 *
 * Example:
 * _successfullySet = ["ammoCount", true, false] call ace_ui_fnc_setAdvancedElement
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_element", "_show", ["_showHint", false, [true]], ["_force", false, [true]] ];

private _cachedElement = GVAR(configCache) getVariable _element;
if (isNil "_cachedElement") exitWith {};

if (!_force && {!GVAR(allowSelectiveUI)}) exitWith {
    [LSTRING(Disallowed), 2] call EFUNC(common,displayTextStructured);
    false
};

_cachedElement params ["_idd", "_elements", "_location", "_conditions"];

// Exit if main vehicle type condition not fitting
private _canUseWeapon = ACE_player call CBA_fnc_canUseWeapon;
if ((_canUseWeapon && {_location == 2}) || {!_canUseWeapon && {_location == 1}}) exitWith {false};

// Get setting from config API
{
    if (!call (_x select 0)) exitWith {
        // Display and print info which component forced the element except for default vehicle check
        if (_showHint) then {
            [LSTRING(Disabled), 2] call EFUNC(common,displayTextStructured);
            INFO_2("Attempted modification of a forced User Interface element '%1' by '%2'.",_element,_x select 1);
        };
        _show = false;
    };
} count _conditions;

// Get setting from scripted API
if (!_force) then {
    private _setElement = GVAR(elementsSet) getVariable _element;
    if (!isNil "_setElement") then {
        _setElement params ["_sourceSet", "_showSet"];
        if (_showHint) then {
            [LSTRING(Disabled), 2] call EFUNC(common,displayTextStructured);
            INFO_2("Attempted modification of a forced User Interface element '%1' by '%2'.",_element,_sourceSet);
        };
        _show = _showSet;
    };
};

_show = [1, 0] select _show;

// Disable/Enable elements
private _success = false;
{
    private _idc = _x;

    // Loop through IGUI displays as they can be present several times for some reason
    {
        if (_idd == ctrlIDD _x) then {
            //TRACE_3("Setting Element Visibility",_show,_idd,_idc);

            (_x displayCtrl _idc) ctrlSetFade _show;
            (_x displayCtrl _idc) ctrlCommit 0;

            _success = true;
        };
    } count (uiNamespace getVariable "IGUI_displays");
    nil
} count _elements;

_success

/*
 * Author: Jonpas
 * Sets basic visible elements of the UI using showHUD setter.
 *
 * Arguments:
 * 0: Show Hint <BOOL> (default: false)
 *
 * Return Value:
 * None
 *
 * Example:
 * [false] call ace_ui_fnc_setElements
 *
 * Public: No
 */
#include "script_component.hpp"

params [["_showHint", false]];

if (isArray (missionConfigFile >> "showHUD")) exitWith {
    if (_showHint) then {
        [LSTRING(Disabled)] call EFUNC(common,displayTextStructured);
    };
};

["ui", [
    true,
    GVAR(soldierVehicleWeaponInfo),
    GVAR(vehicleRadar),
    GVAR(vehicleCompass),
    true,
    GVAR(commandMenu),
    GVAR(groupBar),
    true
]] call EFUNC(common,showHud);

/*
 * Author: Jonpas
 * Setter for toggling advanced element visibility.
 *
 * Arguments:
 * 0: Source <STRING>
 * 1: Set/Unset <BOOL>
 * 2: Element Name <STRING>
 * 3: Show/Hide Element <BOOL> (default: false)
 *
 * Return Value:
 * Successfully Modified <BOOL>
 *
 * Example:
 * _successfullyModified = ["ace_reload", true, "ammoCount", false] call ace_ui_fnc_setElementVisibility
 *
 * Public: Yes
 */
#include "script_component.hpp"

params [
    ["_source", "", [""]],
    ["_set", true, [true]],
    ["_element", "", [""]],
    ["_show", false, [true]]
];

if (_source == "" || {_element == ""}) exitWith {
    WARNING("Source or Element may not be empty strings!");
};

_element = toLower _element;

// Verify element is bound
private _cachedElement = GVAR(configCache) getVariable _element;
if (isNil "_cachedElement") exitWith {
    WARNING_2("Element '%1' does not exist - modification by '%2' failed.",_element,_source);
};

private _setElement = GVAR(elementsSet) getVariable _element;
private _return = false;

if (isNil "_setElement") then {
    TRACE_3("Setting element",_source,_element,_show);
    private _success = [_element, _show, false, true] call FUNC(setAdvancedElement);

    if (_success) then {
        GVAR(elementsSet) setVariable [_element, [_source, _show]];
        _return = true;
    };
} else {
    _setElement params ["_sourceSet"];

    if (_set) then {
        if (GVAR(interfaceInitialized)) then {
            WARNING_3("Element '%1' already set by '%2' - modification by '%3' failed.",_element,_sourceSet,_source);
        };
    } else {
        TRACE_3("Unsetting element",_sourceSet,_element,_show);
        GVAR(elementsSet) setVariable [_element, nil];

        [_element, _show, false, true] call FUNC(setAdvancedElement);
        _return = true;
    };
};

TRACE_2("Visibility set",_element,_return);
_return

