        } else {
            _digit1 = format [QPATHTOF(rsc\vector_%1.paa), _digit1];
            _digit2 = format [QPATHTOF(rsc\vector_%1.paa), _digit2];
            _digit3 = format [QPATHTOF(rsc\vector_%1.paa), _digit3];
        };

    };

    [_digit1, _digit2, _digit3, _digit4]    // return
}

