/*
private "_dlgVector";

disableSerialization;
_dlgVector = GETUVAR(ACE_dlgVector, displayNull);

[
    round parseNumber ctrlText (_dlgVector displayCtrl 156),
    round parseNumber ctrlText (_dlgVector displayCtrl 182)
