/*
 * Author: Ruthberg
 * Displays a wind info (colored arrow) in the top left corner of the screen
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_weather_fnc_displayWindInfo
 *
 * Public: No
 */
#include "script_component.hpp"

#define __dsp (uiNamespace getVariable "RscWindIntuitive")
#define __ctrl (__dsp displayCtrl 132948)

if (GVAR(WindInfo)) exitWith {
    GVAR(WindInfo) = false;
    (["RscWindIntuitive"] call BIS_fnc_rscLayer) cutText ["", "PLAIN"];
    true
};

EGVAR(advanced_ballistics,Protractor) = false;
1 cutText ["", "PLAIN"];
GVAR(WindInfo) = true;


TRACE_1("Starting Wind Info PFEH", GVAR(WindInfo));

[{
    disableSerialization;
    params ["", "_pfID"];

    if ((!GVAR(WindInfo)) || {!([ACE_player, ACE_player, []] call EFUNC(common,canInteractWith))}) exitWith {
        TRACE_1("Ending Wind Info PFEH", GVAR(WindInfo));
        GVAR(WindInfo) = false;
        (["RscWindIntuitive"] call BIS_fnc_rscLayer) cutText ["", "PLAIN"];
        [_pfID] call CBA_fnc_removePerFrameHandler;
    };

    //Keeps the display open:
    (["RscWindIntuitive"] call BIS_fnc_rscLayer) cutRsc ["RscWindIntuitive", "PLAIN", 1, false];

    private _windSpeed = if (missionNamespace getVariable [QEGVAR(advanced_ballistics,enabled), false]) then {
        // With wind gradient
        [eyePos ACE_player, true, true, true] call FUNC(calculateWindSpeed);
    } else {
        // Without wind gradient
        [eyePos ACE_player, false, true, true] call FUNC(calculateWindSpeed);
    };


    private _playerDir = (ACE_player call CBA_fnc_headDir) select 0;
    private _windDir = (wind select 0) atan2 (wind select 1);
    _windDir = 30 * (round(((_windDir - _playerDir + 360) % 360) / 30));

    // Color Codes from https://en.wikipedia.org/wiki/Beaufort_scale#Modern_scale
    private _beaufortNumber = 0;
    private _windColor = [1, 1, 1, 1];
    if (_windSpeed > 0.3) then { _windColor = [0.796, 1, 1, 1]; _beaufortNumber = 1; };
    if (_windSpeed > 1.5) then { _windColor = [0.596, 0.996, 0.796, 1]; _beaufortNumber = 2; };
    if (_windSpeed > 3.3) then { _windColor = [0.596, 0.996, 0.596, 1]; _beaufortNumber = 3; };
    if (_windSpeed > 5.4) then { _windColor = [0.6, 0.996, 0.4, 1]; _beaufortNumber = 4; };
    if (_windSpeed > 7.9) then { _windColor = [0.6, 0.996, 0.047, 1]; _beaufortNumber = 5; };
    if (_windSpeed > 10.7) then { _windColor = [0.8, 0.996, 0.059, 1]; _beaufortNumber = 6; };
    if (_windSpeed > 13.8) then { _windColor = [1, 0.996, 0.067, 1]; _beaufortNumber = 7; };
    if (_windSpeed > 17.1) then { _windColor = [1, 0.796, 0.051, 1]; _beaufortNumber = 8; };
    if (_windSpeed > 20.7) then { _windColor = [1, 0.596, 0.039, 1]; _beaufortNumber = 9; };
    if (_windSpeed > 24.4) then { _windColor = [1, 0.404, 0.031, 1]; _beaufortNumber = 10; };
    if (_windSpeed > 28.4) then { _windColor = [1, 0.22, 0.027, 1]; _beaufortNumber = 11; };
    if (_windSpeed > 32.6) then { _windColor = [1, 0.078, 0.027, 1]; _beaufortNumber = 12; };


    TRACE_3("update display",_beaufortNumber,_windDir,_windSpeed);
    __ctrl ctrlSetTextColor _windColor;
    if (_beaufortNumber > 0) then {
        __ctrl ctrlSetText QPATHTOF(UI\wind_arrow_ca.paa);
        __ctrl ctrlSetAngle [_windDir, 0.5, 0.5];
    } else {
        __ctrl ctrlSetText QPATHTOF(UI\wind_noneCircle_ca.paa);
    };
    __ctrl ctrlCommit 0;

    //Update the beaufort balls:
    (ctrlPosition __ctrl) params ["_ctrlX", "_ctrlY", "_ctrlWidth", "_ctrlHeight"];
    private _centerX = _ctrlX + _ctrlWidth / 2;
    private _centerY = _ctrlY + _ctrlHeight / 2;
    private _ballHeight = _ctrlHeight / 17;
    private _ballWidth = _ballHeight * 3/4;

    for "_index" from 0 to (_beaufortNumber - 1) do {
        private _ball = __dsp ctrlCreate ["RscPicture", _index];
        _ball ctrlSetText QPATHTOF(UI\wind_dot_ca.paa);
        _ball ctrlSetTextColor [1,1,1,1];
        private _ballCenterX = _centerX - (_ballWidth / 2) + ((sin _windDir) * 0.013333) * (_index - 4.9) + ((cos _windDir) * 0.0125);
        private _ballCenterY = _centerY - (_ballHeight / 2) - ((1 * cos _windDir) * 4/3*0.013333) * (_index - 4.9) + ((sin _windDir) * 0.0125);
        _ball ctrlSetPosition [_ballCenterX, _ballCenterY, _ballWidth, _ballHeight];
        _ball ctrlCommit 0;
    };

}, 0.5, []] call CBA_fnc_addPerFrameHandler;

true

/*
 * Author: Glowbal, Ruthberg
 * Module for adjusting the wind deflection settings
 *
 * Arguments:
 * 0: The module logic <LOGIC>
 * 1: units <ARRAY>
 * 2: activated <BOOL>
 *
 * Return Value:
 * None <NIL>
 *
 * Example:
 * [module, [], true] call ace_weather_fnc_initModuleSettings
 *
 * Public: No
 */

#include "script_component.hpp"

params ["_logic", "_units", "_activated"];

if !(_activated) exitWith {};

// Turns the weather module on / off
[_logic, QGVAR(enabled), "enabled"] call EFUNC(common,readSettingFromModule);

// Server weather update interval
[_logic, QGVAR(updateInterval), "updateInterval"] call EFUNC(common,readSettingFromModule);

// Turns the (map based) wind simulation on / off
[_logic, QGVAR(windSimulation), "windSimulation"] call EFUNC(common,readSettingFromModule);

GVAR(updateInterval) = 1 max GVAR(updateInterval) min 600;

/*
 * Author: Ruthberg
 * Inits the wind variables on the server (on mission start)
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_weather_fnc_initWind
 *
 * Public: No
 */
#include "script_component.hpp"

private _month = date select 1;
private _windDirectionProbabilities = GVAR(WindDirectionProbabilities) select (_month - 1);

GVAR(wind_direction_reference) = random 360;
private _sum = 0;
for "_i" from 0 to 7 do {
    _sum = _sum + (_windDirectionProbabilities select _i);
};
private _rand = random _sum;
private _csum = [0, 0, 0, 0, 0, 0, 0, 0];
for "_i" from 0 to 7 do {
    for "_j" from 0 to _i do {
        _csum set [_i, (_csum select _i) + (_windDirectionProbabilities select _j)];
    };
};
private _index = 0;
for "_i" from 0 to 7 do {
    if (_rand > (_csum select _i)) then {
        _index = _index + 1;
    };
};
GVAR(wind_direction_reference) = 45 * _index;

GVAR(wind_mean_dir) = GVAR(wind_direction_reference);
GVAR(wind_direction_reference) = GVAR(wind_direction_reference) + (random 22.5) - (random 22.5);
GVAR(wind_direction_reference) = (360 + GVAR(wind_direction_reference)) % 360;

GVAR(min_wind_speed) = GVAR(WindSpeedMin) select (_month - 1);
GVAR(min_wind_speed) = (GVAR(min_wind_speed) select 0) + (random (GVAR(min_wind_speed) select 1)) - (random (GVAR(min_wind_speed) select 1));
GVAR(min_wind_speed) = 0 max GVAR(min_wind_speed);
GVAR(mean_wind_speed) = GVAR(WindSpeedMean) select (_month - 1);
GVAR(max_wind_speed) = GVAR(WindSpeedMax) select (_month - 1);
GVAR(max_wind_speed) = (GVAR(max_wind_speed) select 0) + (random (GVAR(max_wind_speed) select 1)) - (random (GVAR(max_wind_speed) select 1));
GVAR(max_wind_speed) = 0 max GVAR(max_wind_speed);

GVAR(wind_upper_span) = GVAR(max_wind_speed) - GVAR(mean_wind_speed);
GVAR(wind_lower_span) = GVAR(min_wind_speed) - GVAR(mean_wind_speed);

GVAR(current_wind_direction) = GVAR(wind_direction_reference);
GVAR(next_wind_direction) = GVAR(current_wind_direction);

GVAR(current_wind_speed) = GVAR(min_wind_speed) + (GVAR(max_wind_speed) - GVAR(min_wind_speed)) * (random 1);
GVAR(next_wind_speed) = GVAR(current_wind_speed);

GVAR(last_wind_update) = 0;
GVAR(next_wind_udpate) = 0;

/*
 * Author: ACE2 Team
 * Smoothly updates GVAR(currentHumidity) on the server (based on time of day and map data)
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_weather_fnc_updateHumidity
 *
 * Public: No
 */
#include "script_component.hpp"

if (rain > 0 && overcast > 0.7) then {
    GVAR(currentHumidity) = 1;
} else {
    private _month = date select 1;
    GVAR(currentHumidity) = (GVAR(Humidity) select (_month - 1)) / 100;
    GVAR(currentHumidity) = GVAR(currentHumidity) + GVAR(humidityShift);
    private _avgTemperature = ((GVAR(TempDay) select (_month - 1)) + (GVAR(TempNight) select (_month - 1))) / 2;
    private _pS1 = 6.112 * exp((17.62 * _avgTemperature) / (243.12 + _avgTemperature));
    private _PS2 = 6.112 * exp((17.62 * GVAR(currentTemperature)) / (243.12 + GVAR(currentTemperature)));
    GVAR(currentHumidity) = GVAR(currentHumidity) * _PS1 / _PS2;
};

GVAR(currentHumidity) = 0 max GVAR(currentHumidity) min 1;

publicVariable QGVAR(currentHumidity);

/*
 * Author: ACE2 Team
 * Smoothly updates GVAR(currentTemperature) on the server (based on time of day and map data)
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_weather_fnc_updateTemperature
 *
 * Public: No
 */
#include "script_component.hpp"

private _month = date select 1;
private _timeRatio = abs(daytime - 12) / 12;

GVAR(currentTemperature) = (GVAR(TempDay) select (_month - 1)) * (1 - _timeRatio) + (GVAR(TempNight) select (_month - 1)) * _timeRatio;
GVAR(currentTemperature) = GVAR(currentTemperature) + GVAR(temperatureShift) - GVAR(badWeatherShift) * GVAR(currentOvercast);
GVAR(currentTemperature) = round(GVAR(currentTemperature) * 10) / 10;

publicVariable QGVAR(currentTemperature);

/*
 * Author: ACE2 Team, esteldunedain, Ruthberg
 * Updates the weather evolution on the server. Broadcasts relevant weather information to the clients.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_weather_fnc_updateWeather
 *
 * Public: No
 */
#include "script_component.hpp"

missionNamespace setVariable [QGVAR(currentOvercast), overcast, true];

[] call FUNC(updateTemperature);
[] call FUNC(updateHumidity);

// Wind simulation
if (GVAR(windSimulation) && CBA_missionTime > GVAR(next_wind_udpate)) then {

    GVAR(current_wind_direction) = GVAR(next_wind_direction);
    GVAR(current_wind_speed) = GVAR(next_wind_speed);

    private _transitionPeriod = GVAR(updateInterval) * (2 + (random 4)) / (1 + overcast);
    GVAR(next_wind_udpate) = CBA_missionTime + _transitionPeriod;

    private _windDirectionVariance = (90 - (random 180)) * (overcast ^ 2);
    GVAR(next_wind_direction) = (360 + GVAR(wind_direction_reference) + _windDirectionVariance) % 360;

    if ((random 1) < (0.3 max overcast)) then {
        private _speedVariance = GVAR(wind_upper_span) * (random 1) ^ 2 + GVAR(wind_lower_span) * (random 1) ^ 2;
        GVAR(next_wind_speed) = GVAR(mean_wind_speed) + _speedVariance;
    };

    GVAR(last_wind_update) = CBA_missionTime;

    TRACE_5("dirCur/dirNew/spdCur/spdNew/period",GVAR(current_wind_direction),GVAR(next_wind_direction),GVAR(current_wind_speed),GVAR(next_wind_speed),_transitionPeriod);
};

